css:
@-moz-document url-prefix(http://www.atomix.vg/foros/) 
{
/* vBulletin 3 CSS For Style 'Default Style' (styleid: 1) */
body
{
	background: #1b1b1b !important;
	color: #A4A4A4 !important;
	font: 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0px auto !important;
	
}
/* *** Correct Fixed Width *** */
#content-all { width: 95% !important;  margin-right: auto; margin-left: auto; }
/* *** Block Adds *** */
#slickboxbanner, #nav1, #nav2, #nav-atomix,
div[style="display: block; height: 100px;"],
div[style="margin: 10px auto 0px; width: 728px;"],
table[width="990"]
{ Display:none !important; }
/* *** Left Color *** */
tr[valign="top"] > td.alt2, tr[valign="top"] + tr > td.alt2
{
	width: 200px !important;
	background: #2c2c2c !important;
	color: #A4A4A4 !important;
	font-family: 9pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-left: 1px solid #FFFFFF !important;
	border-top:1px solid #2c2c2c !important;
}
/* *** Quote *** */
td.alt1 > div > div > table > tbody > tr > td.alt2
{
	background: #333333 !important;
	color: #A4A4A4 !important;
	font-family: 9pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-top: 1px solid #333333 !important;
	border-left: 1px solid #333333 !important;
	border-right: 1px solid #777777 !important;
	border-bottom: 1px solid #777777 !important;
}

/* ***** Image fiting***** */

td.alt1 > div[id^="post_message_"] img 
{
max-width: 800px;
cursor: pointer;
}

td.alt1 > div[id^="post_message_"] img:active
{
max-width: 2500px;
}



/* ***** Block color post **** */
font > font > b,
font > b > font,
b > font > font,
td.alt1 > div > b,
td.alt1 > div > font > b,
td.alt1 > div > b > font,
td.alt1 > hr > div > b,
td.alt1 > hr > div > font > b,
td.alt1 > hr > div > b > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > b > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > font > b,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > b,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > font > b,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > b > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > b > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > font > b,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > b,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > font > b,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > b > font
{
	color: #CBCBCB !important;
	text-decoration: none !important;
}

td.alt1 > div > font,
td.alt1 > hr > div > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > font
{	
	color: #ABABAB !important;
	text-decoration: none !important;
}

font[color*="red"] {color: #ff2c2c !important;}
font[color*="blue"] {color: #2c2cff !important;}
font[color*="#000080"] {color: #000080 !important;}
font[color*="#ff66ff"] {color: #ff88ff !important;}



a:link
{
	color: #fa9422 !important;
	text-decoration: none !important;
}

a:visited
{
	color: #ea7412 !important;
	text-decoration: none !important;
}
a:hover, a:active
{
	color: #ff7502 !important;
	text-decoration: underline !important;
}
.page
{
	background: #1b1b1b !important;
	color: #A4A4A4 !important;
	width: 95% !important;
}
.page a:link
{
	text-decoration: none !important;
}
.page a:visited
{
	text-decoration: none !important;
}
.page a:hover, .page a:active
{
	text-decoration: underline !important;
}
td, th, p, li
{
	font: 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tborder
{
	background: #1f1f1f !important;
	color: #A4A4A4 !important;
	border: 1px solid #363636 !important;
}
.tcat
{
	background: #ff7502 !important; 
	color: #FFFFFF !important;
	font: bold 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

.tcat a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tcat a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active
{
	color: #FFFFFF !important;
	text-decoration: underline !important;
}
.thead
{
	background: #373737 repeat-x top left !important;
	color: #FFFFFF !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border: 1px 1px 1px 1px solid #FFFFFF !important;
}
.thead a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.thead a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.thead a:hover, .thead a:active
{
	color: #cdf16c !important;
	text-decoration: underline !important;
}
.tfoot
{
	background: #ff7502 !important;
	color: #FFFFFF !important;
}
.tfoot a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tfoot a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.tfoot a:hover, .tfoot a:active
{
	color: #ff7502 !important;
	text-decoration: underline !important;
}
.alt1, .alt1Active
{
	background: #222222 !important;
	color: #A4A4A4 !important;
	font-family: 9pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-top: 1px solid #222222 !important;
	border-left: 1px solid #222222 !important;
}
.alt2, .alt2Active
{
	background: #222222 !important;
	color: #A4A4A4 !important;
	font-family: 9pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border-top: 1px solid #222222 !important;
	border-left: 1px solid #222222 !important;
}

.inlinemod
{
	background: #333333 !important;
	color: #FFFFFF !important;
	border-top: 1px solid #363636 !important;
	border-left: 1px solid #363636 !important;
}
.wysiwyg
{
	background: #2c2c2c !important;
	color: #A4A4A4 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

input.button
{
	background: #2c2c2c !important;
	color: #747474 !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border: 1px solid #282828 !important;
}
select
{
	color: #ffA542 !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	border: 1px solid #1f1f1f !important;
}
option, optgroup
{
	background: #000000 !important;
	color: #ffffff !important;
	font-size: 11px !important;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

.smallfont
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
        color: #ffffff important;
}

.time
{
	color: #ff7502 !important;
}

/* NavBar CSS*/
.navm
{
	background: #222222 !important;
	color: #FFFFFF !important;
	font: bold 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.navbar
{
	background: #222222 !important;
	color: #FFFFFF !important;
	font: bold 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.navbar a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.navbar a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.navbar a:hover, .navbar a:active
{
	color: #FFFFFF !important;
	text-decoration: underline !important;
}

.highlight
{
	color: #FF0000 !important;
	font-weight: bold !important;
}
.fjsel
{
	background: #000000 !important;
	color: #FFFFFF !important;
}
.fjdpth0
{
	background: #ffA542 !important;
	color: #FFFFFF !important;
}
.panel
{
	background: #2c2c2c !important;
	color: #A4A4A4 !important;
	padding: 10px !important;
	border: 2px outset !important;
}
.panelsurround
{
	background: #282828 !important;
	color: #A4A4A4 !important;
	border-top: 1px solid #363636 !important;
	border-left: 1px solid #363636 !important;
	border-right: 1px solid #363636 !important;
	border-bottom: 1px solid #363636 !important;
}
legend
{
	color: #FFFFFF !important;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

/* ******Vb Menu******* */
.vbmenu_control
{
	background: #ff7502 !important;
	color: #FFFFFF !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
.vbmenu_control a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active
{
	color: #FFFFFF !important;
	text-decoration: underline !important;
}
.vbmenu_popup
{
	background: #1f1f1f !important;
	color: #A4A4A4 !important;
	border: 1px solid #363636 !important;
}
.vbmenu_option
{
	background: #282828 !important;
	color: #A4A4A4 !important;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
	border-top: 1px solid #363636 !important;
	border-left: 1px solid #363636 !important;
}
.vbmenu_option a:link
{
	color: #A4A4A4 !important;
	text-decoration: none !important;
}
.vbmenu_option a:visited
{
	color: #A4A4A4 !important;
	text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active
{
	color: #A4A4A4 !important;
	text-decoration: none !important;
}
.vbmenu_hilite
{
	background: #2c2c2c !important;
	color: #FFFFFF !important;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
	border-top: 1px solid #363636 !important;
	border-left: 1px solid #363636 !important;
}
.vbmenu_hilite a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 14pt !important; }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 4px !important; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none !important; }
.pagenav td { padding: 2px 4px 2px 4px !important; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px !important; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important;}

/* ***** don't change the following ***** */
form { display: inline !important; }
label { cursor: default !important; }
.normal { font-weight: normal !important; }
.inlineimg { vertical-align: middle !important; }

/* ***** Header and Nav  ***** */
.header {
background: #1b1b1b!important ;
border: 0px solid #2c2c2c !important;
}

}
