css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.fileindexer.com/") {

#footer{display:none !important;}
body{max-height:500px  !important;}
#searches100:last-child{display:none !important;}
#addlink_ind:first-child{visibility:hidden !important;}
#stat_ind{visibility:visible !important;}
#header{margin-top:-100px !important;}
h2{margin-top:-40px !important;text-align:center !important;}
h2{font-size:240% !important;}
h2{text-shadow:1px 1px 4px #C43A18 !important;}
#searches100>p{font-family:candara,verdana !important;}
a{color:#09266F !important;}
a:hover{color:#D30909 !important;font-style:oblique !important;}
.head_in_ser{background:#CDD1C5 !important;}
input{color:#D30909 !important;text-transform:uppercase !important;}
#searchForm,#friends,#stat_ind {text-shadow:1px 1px 1px #C43A18 !important;}
#friends>img,#stat_ind>img{padding-left:25px !important;}
}
