css:
@-moz-document url-prefix("https://exvius.gamepedia.com/"), url-prefix("https://exvius.fandom.com/") {
:root {
  --bg-prim: #836028;
  --bg-1: #070504;
  --bg-2: #14100B;
  --bg-3: #201A14;
  --bg-4: #2D251E;
  --bg-5: #3B3127;
  --bg-surface: HSL(30, 10%, 20%);
  --bg-surface-select: HSL(30, 40%, 30%);
  --fg-1: #EFDFCF;
  --fg-2: #CEBFB1;
  --fg-3: #C1AE9C;
  --fg-link: #889AEC;
}
.main-content {
  background: var(--bg-2);
}

#content {
  background-color: var(--bg-1);
  background-image: linear-gradient(180deg, var(--bg-2), var(--bg-1));
  background-size: 100% 200px;
  background-repeat: no-repeat;
}
#searchform input[name="search"] {
  background: var(--bg-5);
  color: var(--fg-1)
}
body {
  color: var(--fg-2);
}
div.thumbinner, table.wikitable > tr > th, table.wikitable > * > tr > th, table.wikitable.mainpage-table > tbody > tr > th, .mp-grid-header {
  background-color: var(--bg-4) !important;
  border-color: var(--bg-5);
}
#toc, .main-content h3, .ibox tr:not(:first-child) th {
  background-color: var(--bg-3);
}
#toc {
  border-radius: 8px;
}
table.wikitable, .mp-grid {
  background-color: var(--bg-2);
}
table.wikitable > tr > td, table.wikitable > * > tr > td, .mp-grid > div, .mp-grid {
  border-color: var(--bg-5)
}
h1, h2, h3, h4, h5, h6, table.wikitable > * > tr > td {
  color: var(--fg-2);
}
div#footer ul li, .quickmenu-table .quickmenu-title, .mainpage-table .quickmenu-title {
  color: var(--fg-3);
}
div#footer {
  background-color: var(--bg-4);
}
h1 {
  border-bottom-color: var(--bg-5);
}
a {
  color: var(--fg-link);
}
.wikitable {
  color: var(--fg-3);
}
.mwe-math-fallback-image-inline {
  filter: invert() hue-rotate(180deg);
}
code, div#content pre {
  color: var(--fg-1);
  background: var(--bg-1) !important;
}
.new {
  color: hsl(9, 70%, 60%) !important;
}
/* 
 Tables
*/
 tr[style*="background-color:#ffd1c9"], td[style*="background-color:#ffd1c9"] {
  background-color: hsl(9, 40%, 10%) !important;
}
tr[style*="background-color:#c9eff1"], td[style*="background-color:#c9eff1"] {
  background-color: hsl(200, 40%, 10%) !important;
}
tr[style*="background-color:#98d9ff"], td[style*="background-color:#98d9ff"] {
  background-color: hsl(200, 40%, 20%) !important;
}
tr[style*="background-color:#ffdab3"], tr[style*="background-color:#ffe5e0"] {
  background-color: hsl(31, 40%, 15%) !important;
}
tr[style*="background-color:#f5c4f5"] {
  background-color: hsl(300, 40%, 15%) !important;
}
.released {
  background: hsl(120, 40%, 20%);
}
.skipped {
  background: hsl(9, 40%, 20%);
}
table.wikitable.navbox {
  border: 1px solid var(--bg-5);
  border-radius: 8px;
  background: var(--bg-4);
}
table.wikitable.navbox + table.wikitable.navbox {
  margin-top: 8px;
}
table.wikitable.navbox > tbody > tr > th {
  background: var(--bg-4);
}
table.wikitable.navbox tr th {
  background: var(--bg-3);
  border: none;
  border-bottom: 1px solid var(--bg-2);
}
.subheader th {
  background-color: var(--bg-3) !important;
}
/*
	Unit List
*/
 .tabulator .tabulator-header {
  border-bottom-color: var(--bg-5);
}
.tabulator .tabulator-header .tabulator-col {
  background: var(--bg-4);
  border-right-color: var(--bg-5);
}
.tabulator .tabulator-header .tabulator-col.tabulator-sortable:hover {
  background-color: var(--bg-surface-select);
}
.tabulator .tabulator-header .tabulator-col.tabulator-sortable[aria-sort="desc"] .tabulator-col-content .tabulator-arrow {
  border-top-color: #FFF;
}
.tabulator .tabulator-header .tabulator-col.tabulator-sortable[aria-sort="asc"] .tabulator-col-content .tabulator-arrow {
  border-bottom-color: #FFF;
}
.tabulator {
  border-color: var(--bg-5);
  background-color: var(--bg-2);
}
.tabulator-row {
  background-color: var(--bg-2) !important;
}
.tabulator-row.tabulator-row-even {
  background-color: var(--bg-3) !important;
}
.tabulator-row .tabulator-cell {
  border-right-color: var(--bg-5);
  border-bottom-color: var(--bg-5);
}
.tabulator .tabulator-tableHolder .tabulator-table {
  color: var(--fg-3);
}
.tabulator-header-filter input {
  background-color: var(--bg-2)
}
input[type="text"], input[name="search"], input, #wpCreateaccount, #wpLoginAttempt {
  border-color: var(--bg-5);
}
.tabulator-edit-select-list {
  background-color: var(--bg-surface);
  border-color: var(--bg-5);
}
.tabulator-edit-select-list .tabulator-edit-select-list-item {
  color: var(--fg-1);
}
.tabulator-edit-select-list .tabulator-edit-select-list-item.active {
  background-color: var(--bg-surface-select);
}
.tabulator-edit-select-list .tabulator-edit-select-list-item:hover {
  background-color: var(--bg-4);
}
.info {
  background-image: url(https://imgur.com/QTuCxon.png);
}
.info:before {
  background-image: url(https://imgur.com/bY7kv4o.png);
}
.info:after {
  background-image: url(https://imgur.com/Lx4vElb.png);
}
/* tabs */
 ul.tabbernav {
  border-bottom: 2px solid var(--bg-5) !important;
  padding: 0;
}
ul.tabbernav li.tabberactive a {
  background-color: var(--bg-5);
  color: var(--fg-1);
  border: none;
  border-radius: 8px 8px 0 0;
}
ul.tabbernav li a {
  background-color: var(--bg-3);
  border: none;
  color: var(--fg-3);
  border-radius: 8px 8px 0 0;
}
ul.tabbernav li a:hover, ul.tabbernav li.tabberactive a:hover {
  background-color: var(--bg-surface-select);
}
td[style*="border-bottom:2px solid #6E9FC1"] {
  border-color: var(--bg-prim) !important;
}
td[style*="background-color:#6E9FC1;"] a, td[style*="background-color:#d7e6f4;"] a {
  color: var(--fg-1);
}
td[style*="background-color:#6E9FC1;"], td[style*="background-color:#d7e6f4;"] {
  border: none !important;
  border-radius: 8px 8px 0 0;
  border-bottom: 2px solid var(--bg-prim) !important;
  /*padding: 10px 16px !important;*/
  min-width: 56px;
}
td[style*="background-color:#d7e6f4;"] {
  background-color: var(--bg-4) !important;
}
td[style*="background-color:#6E9FC1;"] {
  background-color: var(--bg-prim) !important;
}
/* buttons */
 button {
  border-radius: 4px;
  background-color: var(--bg-4);
  color: var(--fg-3);
}
button:hover, button:focus, .button:hover, .button:focus, .zeroclipboard-is-hover {
  background-color: var(--bg-prim);
  color: var(--fg-1);
}
/* JP description card */
 div[style*="background-color:#fffaf2;"] {
  background-color: var(--bg-3) !important;
  border: 2px solid var(--bg-5) !important;
  border-radius: 8px !important;
}
div[style*="background-color:#dcc7a4;"] {
  background-color: var(--bg-5) !important;
}
/* categories */
 #catlinks {
  background: var(--bg-3);
  color: var(--fg-3);
}
#catlinks a {
  color: var(--fg-1)
}
/* enemiy parameters */
 td[style*="139,224,139"] {
  background: HSL(120, 30%, 25%) !important;
  color: var(--fg-1) !important;
}
td[style*="245,152,152"] {
  background: HSL(10, 40%, 25%) !important;
  color: var(--fg-1) !important;
}
.bronze {
  background: HSL(30, 30%, 15%);
  border-bottom: 1px solid #4D627F !important;
}
input[style*="background:#eeeeee"] {
  background: var(--bg-5) !important;
  border-radius: 4px !important;
  margin-bottom: 2px;
}
#filetoc {
  border: none;
  background-color: var(--bg-3);
}
/* search suggestions */
 .suggestions {
  background-color: var(--bg-surface);
  padding: 8px 0;
  border-radius: 8px;
  border-color: var(--bg-5)
}
.suggestions-results {
  background-color: transparent;
  border: none;
}
.suggestions-result {
  display: flex;
  align-items: center;
  padding: 0 8px;
  color: var(--fg-1);
  min-height: 30px;
}
.suggestions-result:hover {
  color: var(--fg-1);
  background: var(--bg-prim)
}
.suggestions-special {
  background-color: transparent;
  border: none;
  display: flex;
  align-items: center;
  padding: 8px;
}
.suggestions-special:hover {
  background-color: var(--bg-prim);
  border: none;
}
.suggestions-special .special-query {
  color: var(--fg-1);
}
/* create */
 .warningbox {
  background: var(--bg-4);
  color: var(--fg-1);
  border-color: var(--bg-5);
  border-radius: 8px;
}
.wikiEditor-ui-toolbar {
  background-color: var(--bg-5);
  background-image: none;
}
.wikiEditor-ui-toolbar .sections .section {
  float: left;
  width: 100%;
  border-color: var(--bg-3);
  background-color: var(--bg-4);
}
#wpTextbox1 {
  background: var(--bg-2);
}
.tool.oo-ui-buttonElement-frameless.oo-ui-iconElement > .oo-ui-buttonElement-button > .oo-ui-iconElement-icon {
  filter: invert();
}
.tool.oo-ui-buttonElement-frameless.oo-ui-iconElement:hover {
  background-color: var(--bg-prim);
  outline: none;
}
#msupload-select {
  filter: saturate(0) brightness(1.75);
}
.wikiEditor-ui-toolbar .tabs span.tab a, .wikiEditor-ui-toolbar .tabs span.tab a:visited {
  color: var(--fg-1)
}
.wikiEditor-ui-toolbar .tabs span.tab a.current, .wikiEditor-ui-toolbar .tabs span.tab a.current:visited {
  color: var(--fg-1);
}
.wikiEditor-ui-toolbar .group .label {
  color: var(--fg-3);
}
.wikiEditor-ui-toolbar .booklet .index .current {
  background-color: var(--bg-prim);
  color: white;
}
.wikiEditor-ui-toolbar .booklet .index div {
  color: var(--fg-3);
}
.wikiEditor-ui-toolbar .booklet .pages {
  background-color: var(--bg-3);
}
.wikiEditor-ui-toolbar .page-characters div span {
  border-color: var(--bg-5);
  color: var(--fg-1);
}
.wikiEditor-ui-toolbar .page-characters div span:hover {
  background-color: var(--bg-prim);
  color: white;
  border-color: var(--bg-5);
}
.wikiEditor-ui-toolbar .page-table td {
  color: white;
  border-top-color: var(--bg-2);
}
.wikiEditor-ui-toolbar .page-table th {
  color: var(--fg-3);
  opacity: 0.7;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button, .oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button {
  display: flex;
  align-items: center;
  min-height: 32px;
  border: none;
  border-radius: 8px;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button {
  background-color: var(--bg-5);
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button:hover {
  background-color: var(--bg-prim);
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button {
  background-color: var(--bg-4);
  color: white;
  border: none;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover {
  background-color: var(--bg-prim);
  color: white;
}
#msupload-div {
  background: var(--bg-surface);
  border-color: var(--bg-surface);
}
.oo-ui-textInputWidget .oo-ui-inputWidget-input {
  background-color: var(--bg-3);
  color: var(--fg-1);
  border-color: var(--bg-5);
}
.wikiEditor-ui .wikiEditor-ui-view {
  border-color: var(--bg-5);
  border-radius: 4px;
}
}
