css:
@-moz-document domain(ava.unicarioca.edu.br) {
  body {
      background-color: #000;
      color: #fff;
  }
  /*cor e borda da navbar*/
  nav.navbar {
      background-color: #000;
      border-top: 1px solid #000;
  }
  /*titulo da navbar*/
  nav.navbar .ambient-type {
      color: #fff;
  }
  /*icones da nav*/
  nav.navbar .nav-item .popover-region .icon {
      color: #fff;
  }
  /*aba ativa*/
  ul.custom-tabs .nav-item .nav-link.active {
      border-color: #e03c3b;
      background-color: #e03c3b;
      color: #fff;
  }
  /*abas nao ativas*/
  ul.custom-tabs .nav-item .nav-link {
      background-color: #eee;
      color: #000;
      border: 1px solid #ddd;
      border-top-left-radius: 5px;
      border-top-right-radius: 5px;
      margin: 0 3px;
      padding: .2rem 1rem;
      font-weight: 600;
      font-size: 13px;
  }
  /*cartoes de disciplina*/
  .card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
      background-color: #111;
      -webkit-background-clip: border-box;
      background-clip: border-box;
      border: 1px solid #111;
  }
  /*titulos do cartao*/
  #page-site-index--custom .card-discipline__content header h3 {
      font-size: 18px;
      font-weight: 600;
      color: #fff;
  }

  #page-course-view-cards .course-card-list .card, #page-course-view-cards .course-card-list #page-enrol-users #filterform, #page-enrol-users #page-course-view-cards .course-card-list #filterform, #page-course-view-cards .course-card-list .que .history, .que #page-course-view-cards .course-card-list .history, #page-course-view-cards .course-card-list .userprofile .profile_tree section, .userprofile .profile_tree #page-course-view-cards .course-card-list section, #page-course-view-cards .course-card-list .groupinfobox, #page-course-view-cards .course-card-list .well {
      box-shadow: 3px 3px 3px rgba(255,0,0,.7);
  }

  .course-header {
      background-color: #000;
  }

  .course-header .course-name {
      color: #fff;
  }

  body:not(#page-site-index--custom) .block-region .card-body, body:not(#page-site-index--custom) .block-region .userprofile .profile_tree section, .userprofile .profile_tree body:not(#page-site-index--custom) .block-region section {
      background-color: #111;
  }

  body:not(#page-site-index--custom) .block-region .card-body .card-title, body:not(#page-site-index--custom) .block-region .userprofile .profile_tree section .card-title, .userprofile .profile_tree body:not(#page-site-index--custom) .block-region section .card-title {
      color: #eee;
  }
  /*extra curriculares*/
  #page-site-index--custom .card-activities__content {
      background-color: #111;
  }

  #page-site-index--custom .card-activities__body h3 a {
      color: #fff;
  }

  #page-site-index--custom .card-filter {
      background: #111; 
      border: 1px solid #111;
  }

  #page-site-index--custom .card-filter select {
      color: #fff;
  }

  .block_enrolledusers .content .userfullname {
      text-decoration: none;
      color: #fff;
  }

  #page-course-view-cards .course-card-list h5.card-title {
      color: #fff;
  }

  /*busca global*/
  section#region-main > .card, #page-enrol-users section#region-main > #filterform, .que section#region-main > .history, .userprofile .profile_tree section#region-main > section, section#region-main > .groupinfobox, section#region-main > .well {
      border-radius: 10px;
      padding: 10px;
  }

  #page-support-campus #help .jumbotron {
      background-color: #111;
  }

  h1, .h1, h2, .h2, .path-calendar .maincalendar .calendar-controls .current, h3, .h3, h4, .h4, h5, .h5, h6, .h6 {
      color: #fff;
  }

  div .editor_atto_content.form-control {
      background-color: #111;
      color: #fff;
      border: 1px solid #111;
  }

  .block_news_items .unlist .post .head .date {
      color: #fff;
  }

  .block_news_items .unlist .post .info a {
      color: #ccc;
  }

  #page-course-view-cards .course-content .section .section .activity {
      background-color: #111;
  }

  #page-course-view-cards .section .activity .activityinstance > a .instancename {
      color: #ccc;    
  }
}
