@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(onlinetimer.ru)
{

body {
    background-color: rgb(48, 48, 51);
    font-family: "Open Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-weight: 300;
    font-size: 14px;
    line-height: 20px;
}

input, textarea, select, button {
    font-family: "Open Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-weight: 300;
}

#body {
    margin: 30px auto;
    width: 800px;
    background-color: rgb(48, 48, 51);
    padding: 20px;
    box-shadow: 0 0 50px rgb(253, 250, 250);
    border: 1px solid #181717;
    position: relative;
}

#logo {
    margin: 20px 0 15px 0;
    display: block;
}

#time-header .my-btn {
    margin: 2px;
}

.timer-display, .timer-display input {
    font-size: 130px;
    line-height: 130px;
}

.timer-display input[type="text"] {
    border: none;
    /*box-shadow: none;*/
    width: 150px;
    /*height: 120px;*/
    text-align: center;
    color: #ffffff;
    background-color: #2f2f31;
    text-shadow: rgb(59, 59, 59) 1px 1px 5px;
}

.timer-display.editing input[type="text"] {
    background-color: rgb(48, 48, 51);
    border: 1px solid #cccccc;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    -webkit-transition: border linear 0.2s, box-shadow linear 0.2s;
    -moz-transition: border linear 0.2s, box-shadow linear 0.2s;
    -o-transition: border linear 0.2s, box-shadow linear 0.2s;
    transition: border linear 0.2s, box-shadow linear 0.2s;
}

.timer-display.editing input[type="text"]:focus {
    border-color: rgba(255, 255, 255, 0.8);
    outline: 0;
    outline: thin dotted  \9;
    /* IE6-9 */

    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);
}

.timer .days {
    font-size: 30px;
}

.timer .days input[type="text"] {
    line-height: 30px;
    width: 70px;
    font-size: 30px;
    text-shadow: #aaa 0 0 2px;
}

.timer table .timer-controls {
    padding: 8px 0 0 10px;
    width: 35px;
    opacity: 0.5;
    line-height: 20px;
    transition: opacity 0.3s;
}

.timer table .timer-controls .my-btn {
    height: 32px;
    width: 32px;
    line-height: 32px;
    font-size: 24px;
    font-weight: normal;
}

.timer table .timer-controls:hover {
    width: 35px;
    opacity: 1;
    transition: opacity 0.3s;
}

/*.timer table .controls:hover {*/
/*opacity: 1;*/
/*}*/

.timer {
    padding: 20px 0;
    color: #ffffff;
}

.timer table {
    margin: 0 auto;
}

.my-btn {
    display: inline-block;
    background-color: #1d1d1f;
    height: 20px;
    line-height: 20px;
    padding: 8px;
    margin: 4px;
    text-align: center;
    cursor: pointer;
    color: white;
    text-decoration: none;
}

.my-btn-small {
    padding: 1px 4px;
}

.my-btn.setup-btn {
    background-color: #000000;
    margin: 1px 3px 0 3px;
}

.times-setup {
    margin-top: -2px;
    border-top: 2px solid #000000;
    padding: 10px;
    background-color: #2f2f31;
    color: rgb(255, 255, 255);
}

.times-setup input {
    font-size: 16px;
}

.timer .description input {
    font-size: 20px;
    line-height: 20px;
    border: 0;
    text-align: center;
    width: 100%;
    color: #999;
    background-color: #2f2f31;
}

.timer .description ::-webkit-input-placeholder {
    /* WebKit browsers */
    color: #ddd;
}

.timer .description :-moz-placeholder {
    /* Mozilla Firefox 4 to 18 */
    color: #ddd;
}

.timer .description ::-moz-placeholder {
    /* Mozilla Firefox 19+ */
    color: #ddd;
}

.timer .description :-ms-input-placeholder {
    /* Internet Explorer 10+ */
    color: #ddd;
}

.my-btn:hover {
    color: white;
}

.my-warning {
    background-color: #000000;
}

.my-error {
    background-color: #000000;
}

.my-error-box {
