css:
@-moz-document url-prefix("http://routerlogin.net/") {
#form {
	background-color: black;
    border: rgba(45,45,45,1) 1px solid;
    border-radius: 6px;
    background-image: none;
    z-index: 999;
}
.basic_icon .icon_name, .basic_icon .icon_status, .basic_icon .icon_long_status {
    color: white;
    background-image: none;
    border-top: rgba(150,150,150,1) 1px solid;
}
#router-name, #firm_version {
    color: white;
}
#firm_version, .logout_bt {
    right: 10px;
}
#router-name {
    left: 10px;
}
#top, .fix_button{
    background-image: none;
	left: 0;
    right: 0;
    border-bottom: rgba(150,150,150,1) 1px solid;
    -webkit-box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
	-moz-box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
	box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    z-index: 99;
}
.label_unclick {
    background-image: none;
    background: linear-gradient(to bottom, rgba(0,0,0,1) 0%, rgba(36,36,36,1) 100%);
    border-radius: 6px 6px 0 0;
    color: gray;
    border-left: rgba(150,150,150,1) 1px solid;
    border-top: rgba(150,150,150,1) 1px solid;
    border-right: rgba(150,150,150,1) 1px solid;
}
.label_click {
    background-image: none;
    background: linear-gradient(to bottom, rgba(36,36,36,1) 0%, rgba(74,74,74,1) 100%);
    border-radius: 6px 6px 0 0;
    color: white;
    border-left: rgba(150,150,150,1) 1px solid;
    border-top: rgba(150,150,150,1) 1px solid;
    border-right: rgba(150,150,150,1) 1px solid;
}
#logo {
    background-image: url('https://imgur.com/2ZGAc25.png');
    left: 10px;
}
body {
    background-color: #0e0e0e;
}
.basic_icon .icon_status b, .basic_icon .icon_long_status b {
    text-shadow: 0px 0px 8px rgba(48, 207, 48, 1);
}
#guest {
    background-image: url('https://imgur.com/ECl1TSL.png');
    transition: transform 0.2s;
}
#parental {
    background-image: url('https://imgur.com/Q89dwos.png');
    transition: transform 0.2s;
}
#internet {
    background-image: url('https://imgur.com/ghZnz4D.png');
    transition: transform 0.2s;
}
#wireless {
    background-image: url('https://imgur.com/hRfBhah.png');
    transition: transform 0.2s;
}
#attached {
    background-image: url('https://imgur.com/6oJPbAB.png');
    transition: transform 0.2s;
}
#readyshare {
    background-image: url('https://imgur.com/3WiFPFF.png');
    transition: transform 0.2s;
}
#attached:hover, #wireless:hover, #internet:hover, #parental:hover, #guest:hover, #readyshare:hover {
  	transform: scale(0.9, 0.9);
}
.footer_img {
    display: none;
}
#footer {
    border-left: rgba(150,150,150,1) 1px solid;
    border-top: rgba(150,150,150,1) 1px solid;
    border-right: rgba(150,150,150,1) 1px solid;
}
.logout_bt {
    background-image: url('https://imgur.com/vXW6Hqp.png');
    background-size: 100% 300%;
    background-position: 0 0;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    height: 19px;
}
.logout_bt:hover {
    background-position: 0 50%;
}
.logout_bt:active {
    background-position: 0 100%;
    transition: background-position 0s;
}
#menu .basic_button_purple, .advanced_purple_button {
    background-image: url('https://imgur.com/vXW6Hqp.png');
    background-size: 100% 300%;
    background-position: 0 100%;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    transform: scale(1, 1);
}
#menu .basic_button, #footer #search .search_button, .com_bt, .middle_common_bt, .reboot, .advanced_white_close_button, .common_bt {
    background-image: url('https://imgur.com/vXW6Hqp.png');
    background-size: 100% 300%;
    background-position: 0 0;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    color: white;
}
#menu .advanced_black_button {
    background-image: url('https://imgur.com/MLfdMdV.png');
    background-size: 100% 300%;
    background-position: 0 0;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    color: white;
}
.advanced_white_open_button {
    background-image: url('https://imgur.com/vXW6Hqp.png');
    background-size: 100% 300%;
    background-position: 0 100%;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    color: white;
}
#menu .basic_button:hover, #footer #search .search_button:hover, .com_bt:hover, .middle_common_bt:hover, .reboot:hover, .advanced_purple_button:hover, #menu .advanced_black_button:hover, .advanced_white_close_button:hover, .common_bt:hover {
    background-position: 0 50%;
    transform: scale(1, 1);
}
#menu .basic_button:active, #footer #search .search_button:active, .com_bt:active, .middle_common_bt:active, .reboot:active, .advanced_purple_button:active, #menu .advanced_black_button:active, .advanced_white_close_button:active, .common_bt:active {
    background-position: 0 100%;
    transition: background-position 0s;
    
}
.adv_icon {
    background-color: black;
    border: rgba(45,45,45,1) 1px solid;
    border-radius: 6px;
    background-image: none;
    z-index: 999;
}
.adv_icon .title {
    background-color: rgba(25,25,25,1);
    border-bottom: rgba(45,45,45,1) 1px solid;
    border-radius: 6px 6px 0 0;
    background-image: none;
    z-index: 999;
}
.apply_bt {
    background-image: url('https://imgur.com/Y2delU4.png');
    background-size: 100% 300%;
    background-position: 0 0;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
}
.apply_bt:hover {
    background-position: 0 50%;
}
.apply_bt:active {
    background-position: 0 100%;
    transition: background-position 0s;
}
.cancel_bt {
    background-image: url('https://imgur.com/mH7ia6B.png');
    background-size: 100% 300%;
    background-position: 0 0;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
}
.cancel_bt:hover {
    background-position: 0 50%;
}
.cancel_bt:active {
    background-position: 0 100%;
    transition: background-position 0s;
}
.adv_icon .info_div, .page_title {
    color: white;
}
TD, b, a {
    background-image: none;
    color: white
}
#content_icons {
    width: 100%;
}
.odd_line, .prgbar {
    background-color: black;
}
h1 {
    background-color: rgba(15,15,15,1);
}
input[type="checkbox"] {
	background-image: url('https://imgur.com/vXW6Hqp.png');
    background-size: 100% 300%;
    background-position: 0 0;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    color: white;
}
.sub_men .sub_back_purple {
    background-image: none;
    transform: scale(1, 1);
}
.common_gray_bt {
    background-image: url('https://imgur.com/MLfdMdV.png');
    background-size: 100% 300%;
    background-position: 0 50%;
    box-shadow: 0px 5px 5px 0px rgba(0,0,0,0.75);
    transition: background-position 0.1s;
    border-radius: 3px;
    color: black;
    position: relative;
    top: 1px;
}
}

@-moz-document url("http://routerlogin.net/top.html") {
html {
    background-color: black;
}
}
