css:
@-moz-document url-prefix("https://schedule.hololive.tv") {
.holodule-drawer > header {
    overflow: hidden!important;
}

.holodule-drawer > header > .drawer-nav > nav {
    overflow-x: hidden!important;
}

div.navbar:nth-child(2) > div:nth-child(1){
    width: 85%!important;
}

@media (min-width: 1625px) {
    .holodule-drawer > header > .drawer-nav > nav {
        width: max-content!important;
        overflow-x: hidden!important;
    }

    .holodule > .container {
        padding-left: 16.25rem!important;
    }

    .holodule .container {
        max-width: 90vw!important;
    }

    div.navbar:nth-child(2) > div:nth-child(1) > div:nth-child(1) {
        padding-left: 16.25rem;
    }
}

@media (min-height: 695px) {
    .holodule-drawer > header > .drawer-nav > nav {
        overflow: hidden!important;
    }
}
}
