@-moz-document domain("inoreader.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.img_format a,
.main-navigation,

a.public_catalog_section_tab,
table.tbl .th td
{
	text-transform: capitalize!important;
}

#sb_rp_mark_read,
	
.article_sub_title .popularity_div_internal_red,
.button,
.img_format,
.inno_toolbar_switcher_button_active,
.inno_toolbar_switcher_button_active:hover,
.item,
.public_catalog_section_tab_current,
.site-main #infinite-handle span button,
.wp-block-button__link,
	
:focus,
button,
input[type=button],
input[type=reset],
input[type=submit]
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.pricing_table_row .pricing_table_cell {
    border-bottom: 1px solid white;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.collection_feed_article_title,

pre code
 {
	font-size: 2.25rem!important;
}

.article_tile_header_date,
.article_unreaded .article_tile_content_wraper.no_pic .article_tile_header_date,
.text,

a.public_catalog_section_tab,
p
 {
	font-size: 1.85rem!important;
}

#discovery_feed_wraper,

.dashboard_gadgets,
.discovery_bundles_floater,
.pricing_feature_row_caption,

table.tbl td
 {
	font-size: 17px!important;
}

.article_sub_title .popularity_score_span
 {
	font-size: 15px!important;
}

.collection_feed_article_title,
.text,

p
{
    line-height: 2.5rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.public_hero_image {
    margin-right: 12px;
}


/* Margins 
_____________________________________________________________________________________________________*/

#ino_content,

.feature
 {
	margin: 12px!important;
}

.social_media_options_footer {
    margin: 24px -5px -3px -5px;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.feature,
.hero,
.serviceList
{
    padding: 20px!important;
}

.article_full_contents,
.public_container
 {
	padding: 12px!important;
}

.public_feed_btn
 {
	padding: 3px 6px!important;
}

.article_sub_title .popularity_div_internal {
    padding: 4px 4px 5px 4px;
}

/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

.collection_feed_article_picture {
    height: 200px;
}

/* Widths 
_____________________________________________________________________________________________________*/

#ino_content,

.bundle_container,
.container,
.feature_text,
.footer_info p,
.footer_info,
.hfeed .site-content,
.public_container,
.width_constraint
 {
	max-width: none!important;
}

selector
 {
	width: auto!important;
}

#ino_content,

.bundle_container,
.container,
.feature,
.hfeed .site-content,
.public_container
 {
	width: 98%!important;
}

.feature_text {
    width: 67%;
}

.collection_feed_article {
    width: 29%;
}

}

