#sb_rp_mark_read,

.article_full_contents,
.article_unreaded .article_tile_content,
.bottom_notification_info,
.feature_text_content,
.inno_toolbar_button_menu,
.inno_toolbar_switcher_button,
.landing_promo_banner_top,
.next_articles,
.public_collection_info_part_description,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#sb_rp_heading,
#signup_dialog_poster_title,

.drag-handle,
.feature_text_h1,
.graylink,
.reader_pane_message span.icon16,
.reader_pane_message,

h1 span,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.article_author,
.article_author span.icon16,

b,
em,
h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.article_tile_header_date,
.article_unreaded .article_tile_content_wraper.no_pic .article_tile_header_date,
.collection_feed_article_date,

[title*="Last post:"],
time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.pricing_plan_price
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("inoreader.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

#landing_wrapper .top_wrapper {
	background-image: none;
}

.dashboard_gadgets .content,
.latest_changelog
{
    background-color: #212020;
}

/* Background 
_____________________________________________________________________________________________________*/

#article_dialog .article_footer_placeholder_top,
#reader_pane,
#sb_rp_tools,
#sb_tree_part,
#subscriptions_buttons,

.article_expanded .article_footer_placeholder_middle.footerized.article_footer_placeholder_middle_sticky,
.dashboard_wrapper,
.drag-handle,
.footer,
.ino_footer,
.landing_sign_or_simply_use,
.pricing_plans_row_sticky,
.pricing_table_row .pricing_table_leftmost_cell,
.pricing_table_row.pricing_feature_row,
.site-header,
.testimonials,
.testimonials_heading,
.top_wrapper
{
	background: #060505!important;
	background-color: #060505!important;
}

.sub_header
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#loader_div,
#sb_tp_search_overlay,

.article_footer,
.bottom_notification_info,
.button,
.button_code,
.card .gray_bgr,
.card .white_bgr,
.inno_toolbar_switcher_button_active,
.inno_toolbar_switcher_button_active:hover,
.item .img_format a:hover,
.next_articles,
.public_hero_container,
.public_main_content,
.serviceList__item:nth-child(even),
.signup_dialog_poster,
.site-main #infinite-handle span button,
.social_media_options_tall .share_img:hover,
.testimonials_heading,
.tr1,
.tr2,
.wp-block-button__link,

button,
button.fb_button,
button.g_button,
input[type=button],
input[type=reset],
input[type=submit]
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.bluebutton, .bluebutton:hover, .bluebutton:disabled, .bluebutton:active, .redbutton, .redbutton:hover, .redbutton:disabled, .redbutton:active, .graybutton, .graybutton:hover, .graybutton:disabled, .graybutton:active {
    line-height: 38px!important;
    height: 42px!important;
}

.a_button,
.bluebutton,
.public_feed_btn,
.public_header_buttons>a.a_button,
.social_media_options_footer,

a.a_button,
a.btn,
a.public_btn
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 0 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.a_button::before,
.bluebutton::before,
.public_feed_btn::before,
.public_header_buttons>a.a_button::before,
.social_media_options_footer::before,

a.a_button::before,
a.btn::before,
a.public_btn::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	padding: 0 1.85rem!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.a_button:hover::before,
.bluebutton:hover::before,
.public_feed_btn:hover::before,
.public_header_buttons>a.a_button:hover::before,
.social_media_options_footer:hover::before,

a.a_button:hover::before,
a.btn:hover::before,
a.public_btn:hover::before
 {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.article_add_tags_button
{	
	background: radial-gradient(circle, #09b705a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.article_add_tags_button::before
{
	background: radial-gradient(circle, #000 0%, #09b705 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.article_add_tags_button:hover::before {
  opacity: 1!important;
}

/* Facebook 
_____________________________________________________________________________________________________*/

button.fb_button
{	
	background: radial-gradient(circle, #537ce2 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

button.fb_button::before
{
	background: radial-gradient(circle, #000 0%, #537ce2 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

button.fb_button:hover::before
 {
  opacity: 1!important;
}

/* Google
_____________________________________________________________________________________________________*/

button.g_button
{	
	background: radial-gradient(circle, #ea4335 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

button.g_button::before
{
	background: radial-gradient(circle, #000 0%, #ea4335 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

button.g_button:hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#ino_content,
#tree_pane,

.article_full_contents,
.container.section,
.feature,
.hero,
.hfeed .site-content,
.public_container,
.serviceList,
.tree_ad
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.article,
.article_tile.article_unreaded.article_expanded,
.article_unreaded,
.card,
.collection_feed,
.collection_heading,
.dashboard_gadgets,
.inno_dialog,
.inno_toolbar_button_menu,
.item,
.public_category_list,
.public_collection,
.read_options,
.sm,
.social_media_options,
.user_testimonial
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#ino_content:hover,
#tree_pane:hover,

.article:hover,
.article_full_contents:hover,
.article_tile.article_unreaded.article_expanded:hover,
.article_unreaded:hover,
.card:hover,
.collection_feed:hover,
.collection_heading:hover,
.container.section:hover,
.dashboard_gadgets:hover,
.feature:hover,
.hero:hover,
.hfeed .site-content:hover,
.inno_dialog:hover,
.inno_toolbar_button_menu:hover,
.item:hover,
.public_category_list:hover,
.public_collection:hover,
.public_container:hover,
.read_options:hover,
.serviceList:hover,
.sm:hover,
.social_media_options:hover,
.tree_ad:hover,
.user_testimonial:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.contact_us .top_section div
{
	background: linear-gradient(to bottom, #2b4973, #000 100%)!important;
}

.contact_us .top_section div.landing_sign_up
{
	background: linear-gradient(to bottom, #243d61, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

.landing_promo_banner_top
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

.pricing_table_row.pricing_feature_row:hover
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.inno_toolbar_button_menu_item:hover,
.parent_div .parent_div_inner:hover,
.parent_div_inner_selected,
.serviceList__item:hover,
.tr1:hover,
.tr2:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

selector
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

selector
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}

.public_catalog_sub_section_tab.public_catalog_sub_section_tab_current,
.public_catalog_section_tab_current
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}
}

