/* Image Settings */

.article,
.article_tile.article_unreaded.article_expanded,
.article_unreaded,
.card,
.collection_feed_article,
.pricing_table_row,
.public_category_list,
.public_collection,
.social_media_options_tall .share_img,
.tr1,
.tr2,
.user_testimonial,

