@-moz-document domain("inoreader.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


.icon14 {
    font-size: 35px;
}

.icon16 {
    font-size: 33px;
}

.share_img.icon16 {
    font-size: 41px;
}

.article_footer .article_footer_buttons {
    font-size: 25px;
}


.pricing_feature_row .pricing_table_cell .icon16.icon_green {
    line-height: 5px;
}

.article_tile_footer_right_buttons .icon16 {
    font-size: 22px;
}

span.icon16.newsletter_retention_icon,
.pricing_feature_description_tip .icon16 {
    font-size: 18px;
}

.popularity_div_internal {
    height: 24px;
}

#tree_pane .feed_favicon {
    top: 8px;
}

/* Annotated _______________________________________________________________*/

.icon-annotation:before {
    content: "";
}

.icon-annotation:before {
	background: url(https://i.ibb.co/RYqxRwQ/highlight.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-annotation:before {
    background-size: 26px!important;
    padding-left: 27px!important;
}


/* Checkmark _______________________________________________________________*/

.icon-upgrade-feature-checkmark:before {
    content: "";
}

#sb_rp_mark_read span.icon16 {
    top: -4px;
}

