Select
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

Select {
    background-size: 21px!important;
    padding-left: 20px!important;
}

/* Coffee _______________________________________________________________*/

.icon-button-mobilize-empty:before {
    content: "";
}

.icon-button-mobilize-empty:before {
	background: url(https://i.ibb.co/Dkfz5zD/Coffee-cup-54x52.png) no-repeat!important;
}

.icon-button-mobilize-empty:before {
    background-size: 28px!important;
    padding-left: 32px!important;
}

/* Fire _______________________________________________________________*/

.icon-fire_fat:before {
    content: "";
}

.icon-fire_fat:before {
	background: url(https://i.ibb.co/4ZrC466/Flaming-Fire-PNG-Clipart-Picture.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-fire_fat:before {
    background-size: 22px!important;
    height: 26px!important;
    padding-left: 22px!important;
    width: 10px!important;
}

/* No Image ___________________________________________________________________________*/

[style="background-image: url('/images/article-image-placeholder-aqua.png')"]
{
	background: url(https://i.ibb.co/fk0y91k/Photos-icon.png) no-repeat!important;
	box-sizing: border-box;
}

[style="background-image: url('/images/article-image-placeholder-aqua.png')"] {
    background-size: 18rem!important;
    height: 19rem!important;
    padding-left: 19rem!important;
    width: 1px!important;
}

/* Mail _______________________________________________________________*/

.icon-button-email:before {
    content: "";
}

.icon-button-email:before {
	background: url(https://i.ibb.co/wY6gQZW/1024px-Mail-closed-svg.png) no-repeat!important;
}

.icon-button-email:before {
    background-size: 28px!important;
    padding-left: 32px!important;
}

/* Open in New Tab _______________________________________________________________*/

.icon-button-open:before {
    content: "";
}

.icon-button-open:before {
	background: url(https://i.ibb.co/k1ghZ14/external-link-red.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-button-open:before {
    background-size: 22px!important;
    height: 26px!important;
    padding-left: 22px!important;
    width: 10px!important;
}

/* Recently Read _______________________________________________________________*/

.icon-recently-read:before {
    content: "";
