 :root {
  --font-primary:'Poppins', sans-serif;
  --font-alternative:'EB Garamond', serif;
  --c-gray: #B5B5B5;
  --c-primary: #948B74;
  --c-secondary: #0deae2;
	--c-secondary--dark: #1eb7b2;
  --max-width: 700px;
}
* {
  margin: 0;
  background: 0;
  box-sizing: border-box;
}
body {
  padding: 0 1rem;
  font-size: 14px;
  color: var(--c-gray);
  background: no-repeat center / cover url('https://images.ctfassets.net/rporu91m20dc/1oQtOXhkWkMQykMisaO8AC/b1fdd9a3dc9e352f26fcd58c7866de6e/Skyrim_Fog_BG.jpg');
}
a {
  color: var(--c-secondary);
  transition: color .3s ease;
}
a:hover {
	color: var(--c-secondary--dark);
}
b {
  color: var(--c-primary);
  font-weight: 600;
}
.s1, span.s1 {
  font: 2em var(--font-alternative);
  font-weight: 600;
  margin-bottom: 1rem;
  padding: 4rem 1rem;
  text-shadow: 0 0 4px rgba(0, 0, 0, 0.5);
  display: block;
  background: no-repeat center / cover url('https://images.ctfassets.net/rporu91m20dc/1tZG7N2uPi8e42seOKEyG8/2c832dfeb8dc3c6a3396b229e6f31de7/hero-bg.jpg?q=70&&&fm=webp');
}
@media screen and (max-width: 770px) {
  .s1, span.s1 {
    margin-left: -1rem;
    margin-right: -1rem;
  }
}
p, p.p1 {
  max-width: var(--max-width);
  margin: 0 auto;
  margin-bottom: 1rem;
  display: block;
  font: 1em var(--font-primary);
}
}
