css:
@-moz-document domain("3.basecamp.com") {
:root {
    --bg-color: #283c46;
    --bg-color-light: #385361;
    --fg-color: #eee;
    --uncolor: #a2afc3;
    --decorated: yellow;
    --progress: #b0b0b0;
    --btn-bg: #385361;
    --btn-border: #1c2a31;
}

body {
    background-color: var(--bg-color);
    color: var(--fg-color);
}

.txt--uncolor {
    color: var(--uncolor);
}

.decorated,
.autolinked,
.chat-line--integration .chat-line__body a,
.formatted_content a:not(.unstyled):not(.attachment__toolbar):not([contenteditable="false"]):not([data-trix-attachment]),
.formatted_content a .mentionable-person,
.recordable--todoset .todolist__permalink {
    color: var(--decorated);
}

.project-index__header,
.project-index__description,
.project-index__templates-callout {
    color: var(--progress);
}

.todo-progress__ratio {
    color: var(--progress);
}

.btn,
.input,
.input--select,
.back-to-top__button {
    background-color: var(--bg-color-light) !important;
    border-color: var(--btn-border);
}

.recording-breadcrumbs,
.card__content,
.panel,
.recordable,
.sheet,
.latest-activity__full-width .activity-item {
    background-color: var(--bg-color);
    color: var(--fg-color);
}

.nav__main,
.nav-menu__sheet,
.report-menu__item--selected,
.nav-menu__scroller {
    background-color: var(--bg-color-light);
}

.break > span {
    background-color: var(--bg-color);
}

.project-avatars__edit::after {
    box-shadow: -4px 0 6px 6px var(--bg-color-light);
}
}
