css:
@-moz-document domain("vk.com") {
.page_block,
.side_bar {
    border-radius: 4px!important;
    box-shadow: 0px 0px 15px -10px rgba(0, 0, 0, calc(0.5 * 1.00))!important;
}
.flat_button,
.button_wide,
input.text.ts_input,
.im_editable {
    box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, calc(0.15 * 1.00))!important;
    transition: box-shadow 0.15s;
}
input[type=text] {
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, calc(0.08 * 1.00))!important;
    transition: box-shadow 0.15s;
}
input.text.ts_input:hover,
.im_editable:hover {
    box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, calc(0.25 * 1.00))!important;
}
.left_menu_nav_wrap {
    display: none!important;
}
.side_bar {
    position: fixed!important;
    padding: 5px 11px;
    padding-bottom: 0px;
    background: white;
    transform: translate(-15px, 7px);
    box-shadow: var(--page-block-shadow);
}

#side_bar {
    top: 57px!important;
}
.side_bar_inner {
    margin-top: 0!important;
    width: 139px!important;
    padding: 0 0 0!important;
}
.side_bar.sticky_top {
    top: 57px!important;
}
#top_profile_menu a:hover {
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, calc(0.15 * 1.00))!important;
}
#side_bar ol li a,
.left_fixer,
.flat_button,
.button_wide,
input.text.ts_input,
.page_actions_item,
#top_profile_menu a,
.im-send-btn.im-send-btn_send {
    border-radius: 4px!important;
    overflow: visible!important;
    display: inline-table;
    transition: transform 0.15s;
    backface-visibility: hidden;
    -webkit-font-smoothing: subpixel-antialiased;
}
#top_profile_menu a {
    transition: background 0.15s, box-shadow 0.15s, transform 0.15s;
    display: block;
}
.flat_button:hover,
.button_wide:hover,
input.text.ts_input:hover,
.page_actions_item:hover,
#side_bar ol li a:active,
.flat_button:active,
.button_wide:active,
input.text.ts_input:active,
.page_actions_item:active,
#top_profile_menu a:active {
    transform: scale(0.97);
}
.left_label {
    overflow: visible!important;
}
.left_settings {
    display: none;
}
#top_profile_menu,
.ui_actions_menu,
._ui_menu {
    box-shadow: 0px 0px 10px 5px rgba(0, 0, 0, calc(0.05 * 1.00))!important;
}
#page_header_cont {
    box-shadow: 0px 0px 15px 5px rgba(0, 0, 0, calc(0.1 * 1.00))!important
}
.top_nav_btn:hover {
    background: unset!important;
}
.top_nav_btn_icon,
.im-send-btn.im-send-btn_send {
    transition: filter 0.1s, transform 0.1s;
    filter: drop-shadow(0px 0px 3px rgba(0, 0, 0, calc(0.15 * 1.00)));
}
.top_nav_btn:hover .top_nav_btn_icon,
.im-send-btn.im-send-btn_send:hover {
    filter: drop-shadow(0px 0px 3px rgba(0, 0, 0, calc(0.40 * 1.00)));
}
.im-send-btn.im-send-btn_send:active {
    transform: scale(0.97);
}
.left_count_wrap {
    padding: 0px 6px!important;
    margin-right: 0px!important;
}
}
