css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {

  body {
    font-size: 60% !important;
  }

  #header_container,
  #menubar,
  #sidebar,
  #footer,
  #interruptor,
  #replyContainer,
  #post-supplement,
  div[class="condensed"],
  div[class^="commenter_tools"],
  div[class="meta-right"],
  div[class="page_navigation permalink_page_navigation"],
  div[class="entry gallery_imageurl gpager_control cn_imageurl"] {
    display: none!important;
  }

  #content_container, #main, #wrapper,
  div[class="caption entry gpager_control cn_imagedescription"],
  div[class="galleryThumbnails"]
 {
    background-image: none!important;
    margin-left: 0px!important; 
    margin-top: 0px!important;
    margin: 0 1% !important;
    text-align: left!important;
    width: 596px !important;
    padding-left: 3px !important;
  }

  #wrapper, #main, #content, div[class="entry"], div[class="post-wrapper"],
  div[class="threadwrapper_invisible only_p1"] {
    margin-left: 1px !important;
    width: 600px !important;
  }

  .post-permalink {
    width:585px;
  }

  #container {
    width: 600px !important;
    font-size: small !important;
  }

  div[class*="commenter_tools"] {
    width: 585px !important;
  }

  #comment_header {
    width: 500px !important;
  }
}
