css:
/*Sitges Film Festival 2020 print friendly*/
 @media print {
    header {
        display:none;
    }
    footer {
        display: none;
    }
    .main {
        margin-top: 0;
    }
    .edition-section.programa .event-section > .container {
        display: none;
    }
    .container-inner {
        width: 100%;
    }
    .blank-space80, .blank-space75 {
        display: none;
    }
    .programa-form {
        margin: 0;
    }
    .edition-category {
        padding: 0;
    }
    .programa-form {
        display: none;
    }
    .title-box {
        display: none;
    }
    .event-section {
        padding: 0;
    }
    #program-table {
        width: 100%;
        font-size: small;
    }
    #program-table a {
        font-size: small;
        font-weight: normal;
    }
    a:after {
        display: none;
    }
    tr.row:before, tr.row:after {
        display: none;
    }
    .fa-hourglass-start {
        display: none;
    }
    #program-table .col-md-3 {
        width: 50%!important;
        padding-left: 10px;
    }
    #program-table .col-md-1 {
        width: 10%!important;
    }
    #program-table td {
        padding: 5px;
        border-width: 1px 0 0 0;
        border-style: solid;
    }
    /* Hora */
    #program-table td:nth-child(2) {
        min-width: 100px;
    }
    /* Avisam, compram */
    #program-table th:nth-child(7), #program-table td:nth-child(7), #program-table th:nth-child(8), #program-table td:nth-child(8) {
        display:none;
    }
}
