css:
@-moz-document url-prefix("https://codeanywhere.com/editor/") {
.hmenu.sm.sm-simple, .vbox, .statusbar{
    background-color: #15132A !important;
}

.multisplit-tabs-wrapper    {
    background-color: #0c0a19 !important;
}

.multisplit .ms-panel .multisplit-tabs-wrapper-bg, .multisplit > .ms-panel, .loaderItem  {
    background: #1D1641 !important;
}


.multisplit .ms-panel .smoothtabs-tab, .smoothtabs-clone, .multisplit .ms-panel .smoothtabs-tabwrap::after {
	background: #1D1641 !important;
}

.multisplit .ms-panel .multisplit-tabs-wrapper-bg {
	background-color: #0af !important;
}

.multisplit .ms-panel .smoothtabs-tabwrap.active .smoothtabs-tab, .smoothtabs-clone.active {
    border-bottom: 3px solid #8300e8 !important;
}
.layout-fit,.gtree .sticky-node,.CodeMirror{
	background-color: #030D22;
}
.CodeMirror .CodeMirror-gutters{
	background-color: #030922;
}

.cm-comment{
	color:#0098DF !important;
}

.CodeMirror .CodeMirror-linenumber, .cm-tag, .cm-meta {
	color:#DF0069 !important;	
}
.cm-string{
	color:#0EF3FF !important;
}

.CodeMirror .cm-attribute, .CodeMirror .cm-property, .cm-variable, .cm-atom {
    color: #FFAF1A !important;
}
.cm-keyword, .cm-operator{
	color:#D625F0 !important;
}


.smoothtabs-tab{
	color:#fff !important;	
}

.active>.smoothtabs-tab {
	color:#1F9AFF !important;	
}

.gtnode.tab::before {
    background-color: #030D22;
}
.activetab, .gtnode.tab.activetab::before {
    background-color: #161130;
}

.openfile-footer {
	background-color: #04112E;
}

.gtnode.folder .gtnode-inner .icon{
	background-image:url(https://img.icons8.com/ios/50/A503B6/folder-invoices--v2.png);
}
.gtnode.file.html .gtnode-inner .icon, .gtnode.file.htm .gtnode-inner .icon, .gtnode.file.xml .gtnode-inner .icon, .multisplit .ms-panel .smoothtabs-tabwrap.file.extension-html .smoothtabs-icon, .multisplit .ms-panel .smoothtabs-tabwrap.file.extension-htm .smoothtabs-icon, .smoothtabs-clone.file.extension-html .smoothtabs-icon, .smoothtabs-clone.file.extension-htm .smoothtabs-icon{
	background-image:url(https://img.icons8.com/ios/50/e34f26/html-5.png);
}
.gtnode.file.js .gtnode-inner .icon, .multisplit .ms-panel .smoothtabs-tabwrap.file.extension-js .smoothtabs-icon, .smoothtabs-clone.file.extension-js .smoothtabs-icon{
	background-image:url(https://img.icons8.com/ios/50/f7df1e/javascript-logo-1.png);
}
.gtnode.file.css .gtnode-inner .icon, .multisplit .ms-panel .smoothtabs-tabwrap.file.extension-css .smoothtabs-icon, .smoothtabs-clone.file.extension-css .smoothtabs-icon{
	background-image:url(https://img.icons8.com/ios/50/1F9AFF/css3.png);
}
.gtnode.file.php .gtnode-inner .icon{
	background-image:url(https://img.icons8.com/ios/50/8892be/php-logo.png);
}
.gtnode.connection.ftp .gtnode-inner .icon{
	background-image:url(https://img.icons8.com/nolan/64/database.png);
}
}
