css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(hup.hu) {

#all, body, .node .links { font-size: 12px !important; }

#top-nav {
  background-image: -moz-linear-gradient(-45deg, #F6F6EB, #D8D8C4, #F6F6EB) !important;
  border: none !important;
}
#primary li {
  text-transform: uppercase !important;
  padding: 0px 8px !important;
  font-weight: bold !important;
}
.title, .content-title {
  border: none !important;
/*  text-shadow: 2px 2px 2px #aaa;
  background-image: -moz-linear-gradient(-45deg, #D8D8C4, #F6F6EB) !important;*/
  background: none !important;
  border-bottom: 2px solid #D8D8D8 !important;
}

.title a { color: #3F3F3F !important; }

tr.even { background: #ddd !important; }


.sidebar .block { border: none !important; }
.sidebar .block h2 {
  background-image: -moz-linear-gradient(-45deg, #F6F6EB, #D8D8C4, #F6F6EB) !important;
}
.poll .title { background: none !important; }
.poll .bar .foreground {
  background: #444 !important;
}

div #block-block-26 { display: none !important; }


.bb-code-block {
  font-family: Consolas,monospace !important;
  font-size: 14px;
  background: #D9D9D9 url(data:image/gif;base64,R0lGODdhAwADAIABANnZ2f///ywAAAAAAwADAAACBEQeZgUAOw==) repeat scroll 0 0;
  border: 1px solid #A5A5A5;
  padding: 5px;
  overflow-x: auto !important;
}

blockquote, #bb-quote-body {
  border-left: 3px solid #ddd;
  color: #555;
  margin-left: 3px;
  padding-left: 9px;
}

div .bb-quote { color: #555555; }

blockquote:before { content: open-quote; font-weight: bold; }
blockquote:after { content: close-quote; font-weight: bold; }

.comment .content { margin-bottom: 0 !important; }
.comment .link {
  margin-top: -8px !important;
  background: none !important;
  border: none !important;
}
.comment, .comment .submitted { border: none !important; }
.comment .submitted {
  background-image: -moz-linear-gradient(-90deg, #D8D8C4, #F6F6EB) !important;
}

.new { display: none; }
.comment-new .submitted:after {
  content: "âš‘";
  color: red;
  font-size: 14px;
  float: right;
  margin-right: 4px;
  margin-top: -2px;
}
}
