css:
@-moz-document domain("lematin.ch") {
[class^='NavigationCategory_dropdown'] [class^='NavigationCategory_list'],
[class^='NavigationCategory_'] [class^='NavigationCategory_list'],
[class^='_app_site'],
[class^='_app_site'][class^='_app_site'] {
    background-color: var(--header-background-color);
}

[class^='Article_article'],
[class^='Rating'],
[class^='Rating'][class^='Rating'],
[class^='Activity'],
[class^='ReportDialog'],
[class^='ReportDialog'][class^='ReportDialog'],
[class^='TeaserContent'],
[class^='Comment'],
[class^='Teaser_teaser'][class^='Teaser_'],
[class^='Article_'][class^='Article_'],
[class^='CockpitNavigation_cockpitMenu'],
[class^='GadgetContainer_cockpitMenu'],
[class^='ActivityBar'][class^='ActivityBar'] {
    background-color: var(--article-background-color);
}

[class^='CommentInput'] {
    color: var(--text-color)
}
}

@-moz-document domain("lematin.ch") {
:root {
    --article-background-color: hsl(15, 80%, 69%);
    --header-background-color: #d34617;
    --titres-color: #fff;
    --text-color: rgb(15, 15, 15);
}
}
