/*General*/
body,
html,
ul,
li {
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
    font-size: 16px;
    line-height: 1.4;
    color: #495057;
}
body,
html {

    text-align: center;
    margin: 0;
    padding: .5rem;
}
body {
    max-width: 100%;
}
a {
    color: #0C3357;
}
a:hover {
    color: #225cff;
}
a:visited {
    color: #1d4eda
}
font {
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
}
input,
select,
textarea {
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
    font-size: 1rem;
    border-radius: .25em;
    border: 1px solid #ced4da;
    padding: .5em;
    background-color: #fff;
    outline: 0;
    vertical-align: middle;
}
FORM#search_form INPUT[type=text] {
    padding: .5em;
    width: 80%;
    max-width: 30em;
    margin-bottom: 1em;
}
FORM#search_form INPUT[type=submit] {
    max-width: 20%;
    margin-bottom: 1em;
}
.inline-fields li:nth-child(2) {
    padding-top: .5em;
}
.inline-fields LABEL,
.inline-selectors LABEL {
    vertical-align: text-top;
}
input[type="text"],
[type="edit"],
[name="req"] {
    box-shadow: inset 0 1px 2px #dee2e6;
}
input[type="text"]:focus,
[type="edit"]:focus,
[name="req"]:focus {
    border-color: #225cff;
}
input[type="radio"],
input[type="checkbox"] {
    margin-bottom: .5em;
    margin-right: .25em;
    margin-left: .25em;
}
td[colspan='4'] input[name="s"] {

    max-width: 48em;
}
select {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    margin: 5px 10px 0 10px;
    box-shadow: inset 0 -1px 2px #dee2e6;
    padding: .5em 1em .5em .5em;
    position: relative;
    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23ced4da'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
    background-size: 12px;
    background-position: calc(100% - 10px) 55%;
    background-repeat: no-repeat;
    padding-right: 30px;
}
select:before {
    content: 'x';
    position: absolute;

    top: 0;
    right: 0;
}

input[type="submit"],
button,
td#info h2 a,
td[bgcolor="#A9F5BC"] a {
    border: 0;
    background-color: #225cff;
    color: #fff;
    font-size: .825rem;
    text-transform: uppercase;
    padding: .65rem 1rem;
    box-shadow: 0 1px 2px #dee2e6;
    border-radius: 3px;
    font-weight: 600;
}
input[type="submit"]:hover,
button:hover,
td#info h2 a:hover,
td[bgcolor="#A9F5BC"] a:hover {

    background-color: #1d4eda;

    box-shadow: 0 1px 2px 2px #dee2e6;
}
label {
    margin-right: .5em;
}


table {
    text-align: left;
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
}
table.c {
    font-family: inherit;
    font-size: .8rem;
}
tr {
    border-bottom-color: transparent;
}
td {
    vertical-align: top;
}

/*----Menu------*/
ul#menu {

    background-color: #ffffff;
    border: 0;
    text-align: center;
    border-bottom: 1px solid #e9ecef;
    box-shadow: 0 2px 2px #e9ecef;
    width: 101%;
    padding: .25em;
    height: auto;
    margin: -1rem -1rem 1rem -1rem;
}
#menu li,
#menu li:hover {
    padding: 0;
    margin: 0;
    display: inline-block;
    float: none;
    border: 0;
}
#menu li:hover {
    background-color: #fff;
    color: #1d4eda;
}
#menu li .drop,
#menu li:hover .drop {
    padding: 8px 30px;
    font-size: .85rem;
    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23adb5bd'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
    background-size: 12px;
    background-position: calc(100% - 10px) 55%;
    background-repeat: no-repeat;
}
#menu li a {
    display: block;
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
    padding: .6em 2em;
    text-transform: capitalize;
}
#menu > li a:hover {

    color: #2a60ff;
    padding: .6em 2em;
    border: 0;
    box-shadow: 0 0 3px #adb5bd;
}
#menu li .drop:hover {
    padding-right: 30px;

    color: #1d4eda;
    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23145591'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
    background-size: 12px;
    background-position: calc(100% - 10px) 55%;
    background-repeat: no-repeat;
    box-shadow: 0 0 3px #e9ecef;
    background-color: #fff;
}

.dropdown_3lettercolumn,
.dropdown_1column,
.dropdown_2columns,
.dropdown_3columns,
.dropdown_4columns,
.dropdown_5columns,
.dropdown_6columns {
    z-index: 100;
    margin: 0px auto;
    float: left;
    position: absolute;
    top: -5px;
    left: -999em;
    text-align: left;
    padding: 1em;
    border: 0;
    /* border-top: none; */
    background: #Fff;
    box-shadow: 0 3px 3px 2px #e9ecef;
    border-radius: 0px 5px 5px 5px;
}

#menu li ul li {

    line-height: 1.3;
    padding: 0;
    width: 98%;
    text-shadow: none;
}
#menu li ul a,
#menu li div a {
    padding: .25em !important;
}
#menu li:hover div a {
    color: #495057;
}
#menu li ul a:hover #menu li div a:hover #menu li li:hover a,
#menu li div div a:hover {
    box-shadow: none;
    background-color: #e9ecef;
    color: #343a40 !important;
    text-shadow: none;
    border-radius: 0;
}
#menu li .dropdown_3lettercolumn .greybox li,
#menu li .dropdown_3lettercolumn .greybox li:hover {
    border: 0;
    padding: 0;
    text-align: center;
}
#menu li .dropdown_3lettercolumn .greybox li a {
    font-weight: bold;
}

#menu p,
#menu h2,
#menu h3,
#menu ul li {
    text-shadow: none;
}
#menu h3 {
    Font-family: "Plex Sans", sans-serif;
    font-size: 15px;
    padding: .25em !important;
    margin: 0;
    border-bottom: 1px solid #adb5bd;
}

#menu li .greybox li {
    background: none;
    border: none;
    margin: 0;
    padding: 0;



    border-radius: 0;
}
#menu li .greybox li a.drop {
    background-color: #fff;
    padding: .75em;
}
#menu li .greybox li:hover {
    background: none;
    border: none !important;
    color: #fff;
    margin: 0;
    padding: 0;
}
#menu li .greybox li:hover a {
    padding: 0;
    text-shadow: none;
    color: #343a40 !important;
}
#menu li .greybox li a.drop:hover {
    background-color: #fff;

    padding: .75em;
}

/*Home*/
caption h1 {
    font-size: 2.4em;
    font-style: normal;
    margin-bottom: 0;
}
form[name="libgen"] {
    padding-top: 1em;
    max-width: 50em;
    line-height: 1.8;
}

form[name="libgen"] b {
    display: inline-block;
    margin-top: 1em;
}
form[name="libgen"] font {
    Font-size: 1.4em;
    font-weight: 600;
    color: #495057;
    display: block;
    margin-top: 1em;
}
form[name="libgen"] font + br {
    display: none;
}
form + font[color="grey"] {
    display: inline-block;
    text-align: center;
    margin: 2rem 0 1rem;
}
font[color="#A00000"] {
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
    color: #0C3357;
}
hr[size="5"] {
    height: 2px;
    background-color: #adb5bd;
    border: 0;
    margin-top: 2em;
}
/*non-fiction*/
h1[style="color:#A00000"] {
    padding: 1em 0 0 0;
    margin: 0;
}
h1 + br + a + em + em + br + a,
h1 + br + a + em + em + br + a + br + a {
    color: 868e96;
    text-decoration: none;
    font-style: italic;
    display: inline-block;
    padding: .5em 0 0;
}
table.c td {
    padding: .5em;
    color: #495057;
}
table.c td td {
    padding: 0 .25em;
}
table td tr {
    background-color: transparent !important;
}
td[width="500"] > a {
    font-weight: bold;
}
td[width="500"] a i {
    font-weight: normal;
}
font[size="1"],
font[size="2"] {
    font-size: 1rem;
    display: inline-block;
    margin-bottom: 1em;
}
/*fiction*/
.header a {
    text-decoration: none;
}
#search_form {
    margin-bottom: 2em;
    overflow: hidden;
}
.catalog_paginator {
    text-align: center;
    font-style: italic;
    color: #868e96;
    margin-bottom: 2em;
}
.catalog_paginator div {
    display: inline-block;
    float: none !important;
}
table[rules="rows"] td {
    padding: .5em;
}
table[rules="rows"] td[width="400"] {
    font-weight: bold;
}
table[rules="rows"] td[width="150"] {
    font-style: italic;
}
table[rules="rows"] td[width="150"] b {
    font-style: normal;
}
.catalog {
    width: 100%;
}
.catalog TD {
    border: 0;
    padding: .5em;
}
.catalog a {
    font-weight: bold;
}
.catalog li a,
.catalog_edit a {
    font-weight: normal;
}
.catalog_edit a {
    font-size: .8em !important;
}
.catalog tr:nth-child(even) td {
    background-color: #f8f9fa;
}
#mlink {
    display: inline-block;
    margin-right: .5em;
}
tr[bgcolor="#C0C0C0"] td,
font + table[rules="rows"] td {
    padding: 1em;
}

tr[bgcolor="#C0C0C0"] td:first-child,
font + table[rules="rows"] td:first-child {
    text-transform: uppercase;
    font-size: .8em;
}
.catalog_authors li a {
    text-transform: uppercase;
    font-size: .8em;
}
.catalog tr td:nth-child(2) {
    font-size: .85em;
    font-style: italic;
    color: #868e96;
}
.catalog tr td:nth-child(4),
.catalog tr td:nth-child(5) {
    font-size: .85em;
    color: #495057;
}
tr[valign="top"] td {
    padding: .5em;
}
tr[valign="top"][bgcolor="#C0C0C0"] td a,
thead td {
    font-size: .75rem;
    text-transform: uppercase;
}
tr[valign="top"][bgcolor="#C0C0C0"],
thead tr {
    border-bottom: 2px solid #dee2e6;
    background-color: white;
}
tr[bgcolor="#C6DEFF"],
table[rules="rows"] tr:nth-child(even) {
    background-color: #f8f9fa;
}
tr[bgcolor="#C6DEFF"] td {
    background-color: #f8f9fa;
}

a[title] {
    font-size: 1rem;
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
}

a[title] font {
    font-size: .8rem;
    color: #868e96;
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
}
p[style="margin:1em 0;text-align:center"],
p[style="margin:1em 0;text-align:center"] a,
caption,
caption a {
    color: #868e96;
    text-decoration: none;
    font-style: italic;
    padding: 1em 0;
}
.paginator {
    margin: 2em .5em;
}
.paginator span strong,
.paginator .current_page_mark {
    background-color: #2a60ff;
}
/*a[href*="/scimag/?q="],
a[href*="/fiction/?q="],*/
.letter_selector LI,
.page_selector + a, div[style="float:right"] a{
    border-radius: 3px;
    padding-top: 6px;
    padding-bottom: 6px;
    border-color: #1d4eda;
    box-shadow: 0 1px 2px #dee2e6;
    display: inline-block;
    font-style: normal;
    position: relative;
    top: 4px;
}
/*a[href*="/scimag/?q="]:hover,
a[href*="/fiction/?q="]:hover,*/
.letter_selector LI:hover,
.page_selector + a:hover {
    border-color: #228be6;
}
a[href*="author"] {
    text-transform: uppercase;
    color: #495057;
}

font[color="green"] {
    color: #868e96 !important;
}

/* Single book view */
td#info {
    max-width: 40em;
    width: 100%;
    display: block;
    margin: 0 auto;
}

td#ad {
    display: none;
}
img[width="240"] {
    display: block;

    margin: 0 auto;
}
td#info {
    max-width: 40em !important;
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
}
td#info h1 {
    text-align: center;
}
td#info h2 a {
    display: inline-block;
    border-radius: .25em;
    text-decoration: none;
}
td[bgcolor="#F5F6CE"] {
    display: none;
}
td[bgcolor="#A9F5BC"] {
    background-color: #fff;
    margin-bottom: 1em;
    padding: .5em;
    border-bottom: 2px solid #e9ecef;
}
td[bgcolor="#A9F5BC"] a {
    color: #fff;
    display: inline-block;
    padding: .25em 2em;
    line-height: 1;
    text-decoration: none;
}
td[bgcolor="#F5F6CE"] + td {

    text-align: center;
    padding: .5em;
}
td[bgcolor="brown"] {
    background-color: #e9ecef;
}
input[size="9"] {
    width: 20em;
    margin: 0 1em;
}

/*table view*/
td[rowspan="22"] + td + td {
    font-size: 120%;
}
table[rules="cols"] {
    /*outline:10px solid red;*/
    border: 0;
    border-spacing: 0;
}
table[rules="cols"] tr,
table[rules="cols"] tbody {

    border-image-width: 0 !important;
    border: 1px solid white !important;
}
table[rules="cols"] tbody > tr > td:first-child,
td[rowspan="22"] + td {
    text-align: right;
}
td[rowspan="22"] {
    text-align: center;
    border-right: 1px solid #868e96;
}
.hashes_header {
    text-align: center;
}
#textarea-example {
    width: 10em;
}
table[rules="cols"] td[colspan="4"] {
    text-align: left !important;
    width: 40em;

    max-width: 100%;
}
a[href="https://forum.mhut.org/viewtopic.php?t=6423"] {
    text-decoration: none;
    padding: 2px 5px;
    border: 1px dashed gray;
    margin-left: 10px;
}
#main {
    max-width: 45em;
}
#main tr:nth-of-type(4) td:nth-of-type(1) {
    display: none;
}

#main tr:nth-of-type(4) td:nth-of-type(2) {
    padding: 1em;
}


#main td[colspan="2"] {

    width: 40em;
}
.record .field {
    text-align: right;
    padding-right: .5em;
    color: #adb5bd;
}
.record .field[colspan="2"] {
    text-align: left;
}
.record td {
    padding-bottom: .25em;
}

ul.record_mirrors li a,
table[rules="cols"] table[rules="cols"] a {
    display: inline-block;
    padding: .5em .75em;
    background-color: #dee2e6;
    border-radius: 3px;
    margin-bottom: .5em;
    box-shadow: 0 1px 2px #f1f3f5;
}
ul.record_mirrors li a:hover,
table[rules="cols"] table[rules="cols"] a:hover {

    background-color: #e9ecef;
}
ul.record_mirrors li:before {
    content: none;
}
ul.record_mirrors li:after {
    content: none;
}
.record_mirrors_compact {
    min-width: 8em;
    text-align: center;
}
/*Scientific articles*/
table[width="1024"] tbody td[width="200"] {
    text-transform: uppercase;
    font-size: .8em;
    color: #495057;
}
table[width="1024"] tbody td[width="300"] {

    font-size: 1.1em;
}
/*x*/
table[width="600"] {
    font-family: "IBM Plex Sans", Roboto, "Open Sans", Calibri, sans-serif;
    font-size: .9rem;
    border: 0 !important;
    line-height: 1.2;
    background-color: white !important;
    border-color: white !important;
    padding: 0;
    box-shadow: 0 0 3px #adb5bd;
    border-collapse: collapse;
}
table[width="600"] td {
    vertical-align: top;
}
table[width="600"] tr:first-child td:nth-child(3) {

    font-weight: bold;
}
table[width="600"] tr:first-child td:nth-child(2),
table[width="600"] tr:nth-child(n + 2) td:nth-child(1) {
    font-size: .8rem;
    text-transform: uppercase;
}

table[width="600"] td[ROWSPAN="2"] {
    font-size: .95rem !important;
    font-style: italic;
    line-height: 1.3;
    text-transform: none !important;
}
}
