@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.taz.de") {
div#hauptbereich {
   position:relative !important;
   top:40px !important;
   width:100% !important;}
div.schwarzer_rahmen_links {margin-top:60px;}
body {background-color:#961D2A !important;}
div#linke_spalte, div.modulkopf, div.modulkoerper, div.layoutmodul, div#content_gesamt {width: auto !important;}
div#main_navi_sub_div, div.artikel, div.primary_teaser, li.teaser, div.secondary_teaser {width: 650px !important;}
div#content_gesamt {background-color:transparent !important;}
div#main_navi_div {width: 100% !important;}
div#main_navi_sub_div {
   background-color:transparent !important;
   float:right !important;
   margin-top: -20px;
   margin-bottom: 0px !important;
   border-left: 20px solid #fff;
   width:auto !important;}
a.main_navi_sub_element_aktiv { background-color:transparent !important;}
div#rechte_spalte {
   width: 250px !important;
   float:right !important;
   padding:16px 0 0 8px !important;
   background-color:#fff;}
div#linke_spalte {padding: 0 12px 0 12px !important; background-color:#fff;}
div.artikel {text-align:justify}
div.ausklapp_plugin_rechts_expanded {margin:0 !important;}
div.ausklapp_plugin_rechts_expanded p {padding:0 !important;}
div.teasertext {
float:none!important;
width: auto !important;
margin-left: 0 !important;
}
.secondary_teaser h2 {
padding-top:0 !important;}
.teaserpic {padding:0 !important; margin: 9px 7px 3px 0 !important; }
div.modulkopf {border-left: solid 19px #000; padding: 3px 0px 0px 3px !important; border-top :3px solid black!important; border-bottom:0 !important;}


