css:
@-moz-document domain("www.lightnovel.us") {
:root {
    --style-opacity: #6cf8f98c;
    --style-light-text: #68fdff;
    --style-light: #44ebed;
    --style: #39d7d9;
    --style-dark: #23c8ca;
    --style-dark-text: #14a7a9;
    --style-white-text: #f7f7f7;
    --warn: red;
    --page-font: '/*[[page-font]]*/';
    --article-font: '/*[[article-font]]*/';
}

:root {
    --accent-coin: #fadd52;
    --accent-star: #b6ed6f;
    --accent-share: #69cff9;
    --accent-up: #ffb236;
}

}

/*[[scheme]]*/

@-moz-document domain("www.lightnovel.us") {
input:focus,
textarea:focus {
    border: 1px var(--style-light) solid;
    box-shadow: none;
}

html,
body {
    background: var(--scheme-background-1);
    font-family: var(--page-font) !important;
}
}

@-moz-document domain("www.lightnovel.us") {
body,
html {
    color: var(--scheme-typography);
}

.text-gray {
    color: var(--scheme-typography-shallow);
}

.nav.flex.jc-between {
    position: sticky;
    z-index: 1000;
    top: -75px;
    background: var(--scheme-background-0);
    color: var(--scheme-typography-bright);
    left: 0;
    transform: translateX(0%);
    transition: top 0.3s cubic-bezier(0.35, 0, 0.25, 1);
    transition-delay: 1s;
    contain: size layout style;
}

.nav.flex.jc-between::after {
    position: absolute;
    content: "";
    display: block;
    background: transparent;
    height: 125px;
    left: 0;
    right: 0;
    z-index: -1;
    top: 0px;
}

.nav.flex.jc-between:hover,
.nav.flex.jc-between:focus-within {
    transition-delay: 0.1s;
    top: 0px;
}

.nav-dltext {
    color: var(--scheme-typography-bright) !important;
}

.nav .left-nav-btns .nav-search {
    border-color: var(--scheme-border);
}

.nav .left-nav-btns .nav-search .nav-search-box {
    background: var(--scheme-background-0);
    color: var(--scheme-typography);
}

.nav .rig-navs-wrapper .login .nav-avatar .nav-avatar-info {
    background: var(--scheme-background-0);
    border-color: var(--scheme-border);
}

.nav .rig-navs-wrapper .login .nav-avatar .nav-avatar-info .nav-avatar-topinfo {
    border-color: var(--scheme-border);
}

.rig-navs-wrapper a {
    color: var(--scheme-typography-bright);
}

.nav.flex .nav-search .nav-hot {
    background: var(--scheme-background-1);
    border-color: var(--scheme-border);
}

.nav.flex .nav-search .nav-hot .hotSearch-text {
    color: var(--scheme-typography-bright);
}

.nav.flex .nav-search .nav-hot .hotSearch-content .hotSearch-content-box {
    background: var(--scheme-background-0);
    border-color: var(--scheme-border);
}

.nuxt-progress {
    transition: width 0.3s linear;
}

.hover,
*[style*='cursor: pointer'] {
    transition: color 0.3s;
}

.hover:hover,
*[style*='cursor: pointer']:hover {
    color: var(--style-dark);
}

*[style*="visibility"] {
    transition: opacity 0.3s, visibility 0.3s;
}

*[style*="visibility: visible"] {
    opacity: 1;
}

*[style*="visibility: hidden"] {
    opacity: 0;
}

.nav-dlQR,
.nav-avatar-info {
    display: block !important;
    transition: opacity 0.3s, visibility 0.3s;
    visibility: visible;
    opacity: 1;
}

.nav-dlQR {
    display: flex!important;
}

.nav-dlQR[style*="display: none"],
.nav-dlQR[style*="display:none"],
.nav-avatar-info[style*="display: none"] {
    visibility: hidden;
    opacity: 0;
}

.nav-dltext {
    transition: color 0.3s;
}

.nav-search {
    transition: border-color 0.3s;
}

.nav .nav-search:hover {
    border-color: var(--style-opacity);
}

.nav-avatar-quit {
    transition: color 0.3s;
}

.nav-avatar-quit:hover {
    color: var(--warn) !important;
}

.nav-message-mark {
    display: none;
}

.nav-button-publish {
    transition: background 0.3s;
    box-shadow: 0 2px 4px 0 var(--style-opacity), 0 2px 8px 0 var(--style-opacity);
}

.nav-button-publish:hover {
    background: var(--style-light) !important;
}

.index-menu {
    background: var(--scheme-background-0) !important;
}

.index-menu li {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 20px !important;
    width: 70px;
}

.index-menu li a {
    color: var(--scheme-typography) !important;
    text-align: center;
    display: block;
    width: 100%;
    transition: color 0.3s;
    word-break: keep-all;
    outline: none;
}

.index-menu li:hover > a,
.index-menu li:focus-within > a,
.index-menu li .btn > a:hover,
.index-menu li .btn > a:focus {
    color: var(--style-light) !important;
}

.index-menu li a::after {
    content: " ";
    position: absolute;
    left: 50% !important;
    bottom: 0 !important;
    width: 0% !important;
    height: 2px;
    background: var(--style);
    border-radius: 1px;
    transition: left 0.3s, width 0.3s;
}

.index-menu li a.active::after {
    left: 0 !important;
    width: 100% !important;
}

.index-menu li:hover > a::after,
.index-menu li:focus-within > a::after,
.index-menu li .btn > a:hover::after,
.index-menu li .btn > a:focus::after {
    left: 0 !important;
    width: 100% !important;
}

.index-menu .container > .index-flow {
    display: none;
}

.index-menu .container ul li .index-flow {
    top: 50px;
    height: 49.9px !important;
    overflow: hidden;
    background-color: var(--scheme-background-0) !important;
}

.index-menu .container ul li:nth-of-type(2) .index-flow .item-box {
    position: relative;
    left: 90px;
}

.index-menu .container ul li:nth-of-type(3) .index-flow .item-box {
    left: 180px !important;
}

.index-menu .container ul li:nth-of-type(4) .index-flow .item-box {
    left: 270px !important;
}

.index-menu .container ul li:nth-of-type(5) .index-flow .item-box {
    left: 360px !important;
}

.index-menu .container ul li:nth-of-type(6) .index-flow .item-box {
    left: 450px !important;
}

.index-menu .container ul li:nth-of-type(7) .index-flow .item-box {
    left: 540px !important;
}

.index-menu .container ul li:nth-of-type(8) .index-flow .item-box {
    left: 630px !important;
}

.index-menu .container ul li:nth-of-type(9) .index-flow .item-box {
    left: 720px !important;
}

.index-menu .container ul li:nth-of-type(10) .index-flow .item-box {
    left: 810px !important;
}

.index-menu .container ul li .index-flow .item-box .btn {
    width: 70px;
    margin-right: 20px !important;
    display: flex;
    justify-content: center;
    align-items: center;
}

.index-menu .container ul li .index-flow {
    display: block !important;
    opacity: 1 !important;
    visibility: visible;
    transition: opacity 0.2s 0s, visibility 0.2s 0s;
}

.index-menu .container ul li a.active + .index-flow {
    display: none !important;
}

.index-menu .container ul li .index-flow[style*="display: none"],
.index-menu .container ul li .index-flow[style*="display:none"] {
    opacity: 0 !important;
    visibility: hidden;
    transition: opacity 0.2s 0.15s, visibility 0.2s 0.15s;
}

.index-menu .container ul li .index-flow .item-box {
    transition: transform 0.15s ease-in, opacity 0.15s ease-in;
}

.index-menu .container ul li .index-flow[style*="display: none"] .item-box,
.index-menu .container ul li .index-flow[style*="display:none"] .item-box {
    transform: translateY(-30px);
    opacity: 0;
    animation: 0.3s ease-out second-level-out;
}

.index-menu .container ul li .index-flow:not([style*="display: none"]):not([style*="display:none"]) .item-box {
    transform: none;
    opacity: 1;
}

@keyframes second-level-out {
    0% {
        transform: none;
        opacity: 1;
    }
    50% {
        transform: translateY(15px);
        opacity: 0;
    }
    100% {
        transform: translateY(30px);
        opacity: 0;
    }
}

.rank-item {
    outline: none;
}

.rank-item .rank-title,
.rank .title {
    color: var(--scheme-typography) !important;
    transition: color 0.3s;
}

.rank-item .rank-num {
    background-color: var(--scheme-background-4) !important;
    transition: background 0.3s;
}

.rank-item:first-of-type .rank-num {
    z-index: 1;
    background-color: var(--style) !important;
}

.rank-item:not(:first-of-type) .rank-num {
    margin-top: 1.5px
}

.module-right .qr-code {
    border-color: var(--scheme-border) !important;
}

.module-right .qr-code .QRtext {
    color: var(--scheme-typography) !important;
}

.rank-item:hover .rank-title,
.rank-item:focus-within .rank-title {
    color: var(--style-dark) !important;
}

.rank-item:hover .rank-num,
.rank-item:focus-within .rank-num {
    background-color: var(--style-light) !important;
}

.rank-item .rank-title:not(.other) {
    height: 54px;
    transition: color 0.3s, height 0.3s ease-in-out 0.5s;
}

.rank-item:hover .rank-title:not(.other),
.rank-item:focus-within .rank-title:not(.other) {
    height: 90px;
    -webkit-line-clamp: 5 !important;
    transition: color 0.3s, height 0.3s ease-in-out 0s;
}

.rank .rank-list a.rank-item .rank-detail .card-info {
    color: var(--scheme-typography-shallow) !important;
}

.rank .rank-list a.rank-item .rank-detail .card-info svg {
    fill: var(--scheme-typography-shallow) !important;
}

.rank .rank-list a.rank-item.other  {
    height: 18px !important;
    overflow: visible;
}

.rank .rank-list a.rank-item.other .rank-title.other {
    position: relative;
    display: block;
    color: var(--scheme-typography) !important;
    overflow: visible;
    font-size: 0;
    line-height: 0;
    width: 100%;
    pointer-events: none;
}

.rank .rank-list a.rank-item.other .rank-title.other::before {
    display: block;
    content: attr(title);
    font-size: 13px;
    line-height: 18px;
    overflow: hidden;
    width: 100%;
    text-overflow: ellipsis;
    white-space: pre;
}

.rank .rank-list a.rank-item.other:hover .rank-title.other,
.rank .rank-list a.rank-item.other:focus-within .rank-title.other {
    color: var(--style-dark) !important;
}

.rank .rank-list a.rank-item.other .rank-title.other::after {
    z-index: 1;
    color: var(--scheme-background-1) ;
    background-color: var(--scheme-typography-bright);
    --ani-span: 150ms;
    content: attr(title);
    position: absolute;
    word-break: break-all;
    white-space: normal;
    display: block;
    border-radius: 5px;
    padding: 6px 5px;
    font-size: 12px;
    line-height: 15px;
    transition: opacity var(--ani-span) ease-in, bottom var(--ani-span) ease-in-out, clip-path var(--ani-span) ease-in-out var(--ani-span);
    opacity: 0;
    clip-path: circle(0 at 50% 100%);
    max-width: calc(100% - 2em);
    bottom: 100%;
}

.rank .rank-list a.rank-item.other:hover .rank-title.other::after,
.rank .rank-list a.rank-item.other:focus-within .rank-title.other::after {
    --ani-span: 250ms;
    transition-delay: 0ms;
    opacity: 1;
    clip-path: circle(200% at 50% 150%);
    bottom: calc(100% + 7px);
}


.el-pager .number,
.my-pagination .el-pager .btn-quicknext,
.my-pagination .el-pager .btn-quickprev,
.my-pagination .btn-prev,
.my-pagination .btn-next {
    color: var(--scheme-typography);
    background: var(--scheme-background-2) !important;
    border-color: var(--scheme-border) !important;
    transition: color 0.3s, background 0.3s;
}

.my-pagination .el-pager .btn-quicknext,
.my-pagination .el-pager .btn-quickprev {
    border: 1px solid;
    border-radius: 5px;
}

.my-pagination .el-pager .number.active {
    background: var(--style) !important;
}

.my-pagination .el-pager .number:hover,
.my-pagination .el-pager .btn-quicknext:hover,
.my-pagination .el-pager .btn-quickprev:hover,
.my-pagination .btn-prev:not([disabled]):hover,
.my-pagination .btn-next:not([disabled]):hover {
    color: var(--style-white-text);
    background: var(--style-light) !important;
}

.my-pagination .btn-prev[disabled] {
    color: var(--scheme-typography-shallow);
}

.el-pagination__jump {
    color: var(--scheme-typography);
}

.my-pagination .btn-prev,
.my-pagination .btn-next,
.el-input .el-input__inner,
.el-pager .btn-quicknext,
.el-pager .btn-quickprev {
    height: 30px !important;
    line-height: 30px !important;
    background: var(--scheme-background-2);
    border-color: var(--scheme-border);
    color: var(--scheme-typography);
}

.el-input .el-input__inner:hover {
    border-color: var(--style-opacity) !important;
}

.el-input .el-input__inner:focus {
    border-color: var(--style-light) !important;
}

.footer {
    background: var(--scheme-background-3) !important;
}

.user-avatar-container {
    border-color: var(--scheme-border);
}

.bg-gray-f7 {
    background: var(--scheme-background-2);
}

.bg-gray-f7 .containner .sidebar,
.bg-gray-f7 .containner .sidebar .sidebar-menus,
.bg-gray-f7 .containner .contents,
.bg-gray-f7 .containner .articles-container,
.bg-gray-f7 .containner .medal-wall-container,
.bg-gray-f7 .message-container .sidebar-menu,
.bg-gray-f7 .msg-comment-reply .top-title,
.bg-gray-f7 .msg-comment-reply .messages,
.bg-gray-f7 .profile-page .sidebar,
.profile-page .right-contents-container .bg-white-item {
    background: var(--scheme-background-0);
}

.bg-gray-f7 .containner .line,
.bg-gray-f7 .message-container .line,
.bg-gray-f7 .profile-page .line {
    background: var(--scheme-background-2) !important;
}

.text-dark,
.nuxt-link,
.link {
    color: var(--scheme-typography);
}

.bg-gray-f7 .containner .sidebar .sidebar-menus .menu-item:last-of-type span,
.bg-gray-f7 .containner .sidebar .sidebar-menus .menu-item:first-of-type .item-text {
    color: var(--scheme-typography);
}

.bg-gray-f7 .containner .sidebar .sidebar-menus .menu-item .item-text {
    color: var(--scheme-typography-shallow);
}

.articles-container .tabbar-item {
    color: var(--scheme-typography);
}

.el-dialog {
    background: var(--scheme-background-0);
}

.el-dialog__title {
    color: var(--scheme-typography-bright);
}

.collection-item-container,
.msg-comment-reply .messages .message-item {
    border-color: var(--scheme-border);
}

.publish_mgr_container .right-btns.btn-select-muilt {
    border-color: var(--scheme-border);
    color: var(--scheme-typography);
}

.bg-gray-f7 .contents select.select,
.bg-gray-f7 .contents input,
.el-dialog input,
.el-dialog textarea,
.el-dialog select.select {
    color: var(--scheme-typography);
    background-color: var(--scheme-background-1);
    border-color: var(--scheme-border);
    transition: border 0.3s;
}

.bg-gray-f7 .contents select.select:hover,
.bg-gray-f7 .contents input:hover,
.el-dialog input:hover,
.el-dialog textarea:hover,
.el-dialog select.select:hover {
    border-color: var(--style-opacity);
}

.bg-gray-f7 .contents select.select:focus,
.bg-gray-f7 .contents input:focus,
.el-dialog input:focus,
.el-dialog textarea:focus,
.el-dialog select.select:focus {
    outline: none;
    border-color: var(--style);
}

.el-card {
    color: var(--scheme-typography);
    border-color: var(--scheme-border);
    background: var(--scheme-background-1);
}

.medal-steps .medal-img-container,
.medal-card {
    border-color: var(--scheme-border);
}

.medal-name,
.medal-wall-title {
    color: var(--scheme-typography);
}

.medal-step-button .el-button.is-disabled,
.medal-step-button .el-button.is-disabled:hover,
.medal-step-button .el-button.is-disabled:focus {
    color: var(--scheme-typography-shallow);
    border-color: var(--scheme-border);
    background: var(--scheme-background-3);
}

.medal-step-line {
    background: var(--scheme-background-3);
}

.medal-step-line-inner {
    border-color: var(--scheme-background-3);
}

.user-avatar-container {
    overflow: hidden;
}

.avatar {
    object-fit: cover;
    text-indent: -10000px;
    position: relative;
}

.avatar:-moz-broken {
    width: 100px !important;
    height: 100px !important;
    margin: -20px -20px;
    background-size: 55px;
    background-position: center;
    background-image: url(https://static.lightnovel.us/default_avatar.jpg);
}

.avatar::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    text-indent: 10000px;
    background-size: cover;
    background-position: center;
    background-image: url(https://static.lightnovel.us/default_avatar.jpg);
    z-index: 1;
}
}

@-moz-document regexp("^https?://(www\\.lightnovel\\.us)/(cn/)?$") {
.index-container .module-index .module-left .module-head .module-title .icon-title {
    color: var(--scheme-typography) !important;
}

.module-list .article-item-title {
    -webkit-line-clamp: 2;
    line-height: 1.5;
    height: 3em;
}

.swiper-title {
    transition: color 0.3s;
}

.swiper-wrapper:hover .swiper-title {
    color: var(--style-light-text) !important;
}

.recommend-item .mask {
    transition: height 0.3s ease-in-out 0.3s;
}

.recommend-item .title {
    height: 2.8em;
    transition: height 0.3s ease-in-out 0.3s, color 0.3s;
}

.recommend-item:hover .mask {
    height: 90px;
    transition: height 0.3s ease-in-out 0s;
}

.recommend-item:hover .title {
    -webkit-line-clamp: 3;
    height: 4.2em;
    color: var(--style-light-text);
    transition: height 0.3s ease-in-out 0s, color 0.3s;
}

.module-index {
    overflow: visible !important;
}

.module-icon {
    transition: transform 0.3s;
}

.module-index:hover .module-icon {
    transform: scale(1.1);
}

.article-item-title,
.module-more {
    transition: color 0.3s;
}

.module-more:hover {
    color: var(--style-light) !important;
}

.article-item-container,
.article-item-container .article-item-title,
.article-item-container .article-item-img-container {
    cursor: alias !important;
}

.article-item-container .article-item-title {
    position: relative;
    display: block;
    color: var(--scheme-typography) !important;
    overflow: visible;
    font-size: 0;
    padding-bottom: 45px;
    pointer-events: none;
}

.article-item-container .article-item-title::before {
    display: -webkit-box;
    content: attr(title);
    font-size: 15px;
    height: 3em;
    overflow: hidden;
    width: 100%;
    text-overflow: ellipsis;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    word-break: break-all;
}

.article-item-container:hover .article-item-title {
    color: var(--style-dark) !important;
}

.article-item-container .article-item-title::after {
    color: var(--scheme-background-1) ;
    background-color: var(--scheme-typography-bright);
    --ani-span: 150ms;
    content: attr(title);
    position: absolute;
    word-break: break-all;
    white-space: normal;
    display: block;
    border-radius: 5px;
    padding: 6px 5px;
    font-size: 12px;
    line-height: 15px;
    transition: opacity var(--ani-span) ease-in, bottom var(--ani-span) ease-in-out, clip-path var(--ani-span) ease-in-out var(--ani-span);
    opacity: 0;
    clip-path: circle(0 at 50% 100%);
    max-width: 160px;
}

.article-item-container:hover .article-item-title::after {
    --ani-span: 250ms;
    transition-delay: 0ms;
    opacity: 1;
    clip-path: circle(200% at 50% 150%);
}

.article-item-container .article-item-img-container.vertical + .article-item-title::after {
    bottom: calc(100% + 110px);
}

.article-item-container .article-item-img-container.horizontal + .article-item-title::after {
    bottom: calc(100% + 235px);
}

.article-item-container:hover .article-item-img-container.vertical + .article-item-title::after {
    bottom: calc(100% + 120px);
}

.article-item-container:hover .article-item-img-container.horizontal + .article-item-title::after {
    bottom: calc(100% + 250px);
}

.article-item-container .article-item-img-container {
    background-color: var(--scheme-background-2);
    border-color: var(--scheme-border);
    transition: transform 0.2s cubic-bezier(0.35, 0, 0.25, 1);
    will-change: transform;
}

.article-item-container:hover .article-item-img-container {
    transform: scale(1.08);
}

.article-item-container .article-item-img-container img {
    margin: -1px -4px;
    width: calc(100% + 8px);
    height: calc(100% + 4px);
}

.fixed-download {
    color: var(--scheme-typography) !important;
    background-color: var(--scheme-background-2) !important;
    border: none !important;
}

.fixed-download .bg-white {
    background-color: var(--scheme-background-2);
}
}

@-moz-document regexp("^https?://(www\\.lightnovel\\.us)/(cn/)?category/.+$"), regexp("^https?://(www\\.lightnovel\\.us)/(cn/)?series/.+$") {
.tags-menu .tag {
    border-color: var(--scheme-border) !important;
    color: var(--scheme-typography) !important;
    transition: color 0.3s, background 0.3s;
}

.tags-menu .tag:hover {
    background: var(--style-light) !important;
}

.tags-menu .tag:hover,
.tags-menu .tag.nuxt-link-active {
