css:
@-moz-document domain("imdb.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

#pagecontent,
div#content-2-wide,

.even,

#content-2-wide:before,

#styleguide-v2, #styleguide-v2 #wrapper, #root
{
  background: #3d3d3f;
}

.heroic-overview,
.add-image-container,

.odd,

.article,
.aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4
{
    background: #363639;
}

.recently-viewed,

#filmography .head,

.redesign .article.highlighted,

select,

.faceter>.faceter-header,
.lister .header,

.lister-item.mode-detail,

.ipl-zebra-list .ipl-zebra-list__item:nth-of-type(1n),
.ipl-itemcount-header,

#prometer, #prometer #meterRank
{
    background: #2e2e2f !important;
}

#sidebar div:last-child *,

.rec_details .rec-jaw-upper,
.rec_details .rec-jaw-lower>.rec-jaw-teeth, .rec_details .rec-jaw-lower,
.lister .header > div
{
    background: #fff0 !important;
}

.lister .lister-working
{
    background: #2e2e2fcf !important;
}

.user-lists .ipl-zebra-list .ipl-zebra-list__item .description__fade:after
{
background: linear-gradient(rgba(251,251,251,0), #2e2e2f 80%, #2e2e2f);}

body, h1, h2, h3, h4, select,
#root,

.cast_list .character,
.imdbRatingPlugin,

h1, .article h1.header span.itemprop,

.rightcornerlink span,

.btn2,

.text-primary
{
  color: #ccc !important;
}

.text-muted
{
  color: #cccccc82 !important;
}

*,
#content-2-wide:before
{
  border: none !important;
  box-shadow: none !important;
}

#filmography .head
{
  text-shadow: none;
}

clearImage 
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}
}
