css:
@-moz-document domain(keystone-apps.stpeters.sa.edu.au) {
	:root {
    --background-dark: #121212;
    --background: #2d2d2d;
    --light: #fff;
    --primary: #507DBC;
    --primary-dark: #395D93;
    --primary-light: #D3DEEE;
}
.content-well, .mt-box-content, .mt-box-item, .mt-box-btn-list, .ks-footer-light, #yearSubjectSelector, .panel, .ks-card, td, .ks-peoplepicker .kspp-popup, .ks-peoplepicker .kspp-popup LI, .ks-panel-menu, .dropzone, .dropdown-menu, .btn-default:hover, .btn-default:focus, select:hover, input:hover, input:focus, .well {
    background-color: var(--background) !important;
}
.ks-wallpaper, body, html, .ks-search-overlay, .t4:hover, .dropzone:hover, .btn-default, input, select, .dropdown-menu li a:hover, .panel-heading {
    background-color: var(--background-dark) !important;
}
.ks-heading-link, .page-header, #divTasks, .mt-active-message, .text-center-xs, .text-center, .tt-grid-day-header, .vis-text, h1, h2, h3, h4, h5, h6, .row div, li span, .clearfix span, .ks-tile, .media-body, .list-inline-item, .wb-result-grid, .ks-pills li, .ks-menu-item, .ks-menu-sub-item, .ks-panel-list, .ks-w-observation, .ks-w-extended-forecast, .ks-result-tile, .kspp-header, sz-choice-form-wrapper, .btn-default, input, select, .dropdown-menu li a, .table, ks-tickbox div span, .ks-menu-results div, .well, .content-well {
    color: var(--light) !important; 
}
.fa-inactive {
    color: var(--background-dark) !important;
}
#sportzone .sz-week .sz-week-nav>I:not(.fa-inactive):hover, .fa {
    color: var(--light);
}
.vis-item-content .fa {
    color: inherit !important;
}
.ks-wallpaper img {
    display: none;
}
.btn-primary, .btn-play, div.btn-default {
    background-color: var(--primary) !important;
    border-color: var(--primary-dark);
    color: var(--light);
}
.btn-primary:hover, .btn-play:hover {
    background-color: var(--primary) !important;
}
.ks-navbar, .ks-panel-overview, .ks-panel-list li, .ks-result-tile, .kspp-header {
    background-color: var(--background) !important;
}
a {
    color: var(--primary);
}
.vis-item, input, select, .btn-default {
    border: 1px solid black !important;
}
svg {
    background-color: var(--light);
}
/* Background */
#ks-home, #ks-content {
    background: var(--background-dark) !important;
    background-size: initial !important;
}
.ks-tile {
    background: var(--primary-dark) !important;
}
#timetable .tt-grid-cell.tt-lesson {
    background-color: var(--background);
    color: var(--light);
}
#timetable .tt-now {
    background-color: var(--primary) !important;
    color: var(--light) !important;
    border-radius: 5px;
}
div[data-bind="html: Notice"], div.bold, .dn-blog, #muster-text {
    color: var(--light);
}
#timetable .tt-grid-cell.tt-lesson, #timetable .tt-table tr td, .ks-card-block {
    border: none;
}
#timetable .tt-grid-cell.tt-break, .breadcrumb {
    background: repeating-linear-gradient(
      45deg,
      var(--background),
      var(--background) 10px,
      #373737 10px,
      #373737 20px
    );
    color: white;
}
#timetable .tt-grid-cell.tt-break {
    border: 1px solid #373737;
}
}
