css:
@-moz-document domain("sky.shiiyu.moe") {
a
  {
    color: #7652b1;
  }

  .external-app-name
  {
    color: #7652b1;
  }

  #goto_target_username
  {
    background-color: #7652b1;
  }

  #goto_target_username:hover
  {
    background-color: #ba5fde;
  }

  .additional-player-stat
  {
    background-color: #7652b1;
  }

  .additional-player-stat:hover
  {
    background-color: #ba5fde;
  }

  .skill-progress-bar
  {
    background-color: #ba5fde;
  }

  .skill-icon
  {
    background-color: #ba5fde;
  }

  .skill-bar::before
  {
    background-color: #ba5fde;
  }

  #nav_bar_line
  {
    background-color: #7652b1;
  }

  .nav-item::after
  {
    background-color: #ba5fde;
  }

  .stat-header span
  {
    border-bottom: 4px solid #7652b1;
  }

  .inventory-header-line
  {
    background-color: #7652b1;
  }

  #inventory_header .inventory-tab::after
  {
    background-color: #7652b1;
  }

  .sea-creature-name
  {
    border-bottom: 2px solid #7652b1;
  }

  .narrow-info-header
  {
    border-bottom: 2px solid #7652b1;
  }

  .kills-deaths-container .show-all
  {
    color: #7652b1;
  }

  .tippy-tooltip[data-placement^="top"] > .tippy-arrow
  {
    border-top-color: rgb(186, 95, 222, 9);
  }

  .tippy-tooltip[data-placement^="bottom"] > .tippy-arrow
  {
    border-bottom-color: rgb(186, 95, 222, 9);
  }

  .info-header
  {
    background-color: #7652b1;
  }

  .pride-flag.logo
  {
    background: url(https://i.imgur.com/qmAi46B.png);
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 100%;
    background-position: center;
    background-repeat: no-repeat;
    width: 25px;
    height: 25px;
    margin-top: 12px;
  }

  html
  {
    background-color: #000;
    background: url(https://i.imgur.com/iVH50S0.png) no-repeat center center fixed;
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 105%;
    background-position: bottom;
    background-repeat: no-repeat;
  }

  #bg_blur
  {
    background: url(https://i.imgur.com/UJGBnPr.png) no-repeat center center fixed;
    background-color: rgba(0, 0, 0, 0);
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 105%;
    background-position: bottom;
    background-repeat: no-repeat;
  }

  #enter_player_box_wrapper
  {
    background: url(https://i.imgur.com/UJGBnPr.png) no-repeat center center fixed;
    background-color: rgba(0, 0, 0, 0);
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 105%;
    background-position: bottom;
    background-repeat: no-repeat;
  }

  #error_title
  {
    background-color: #ba5fde;
  }

  #error_box_wrapper
  {
    background: url(https://i.imgur.com/UJGBnPr.png) no-repeat center center fixed;
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 105%;
    background-position: bottom;
    background-repeat: no-repeat;
  }

  [data-tippy-content]:hover:after
  {
    color: #8d42bf;
  }

  .top-profile
  {
    background: url(https://i.imgur.com/UJGBnPr.png) no-repeat center center fixed;
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 105%;
    background-position: bottom;
    background-repeat: no-repeat;
  }

  .switch_packs_button
  {
    background-color: #7652b1;
  }

  .switch_packs_button:hover
  {
    background-color: #ba5fde;
  }

  .kills-deaths-container .narrow-info-content
  {
    scrollbar-color: #7652b1 rgba(0, 0, 0, 0);
  }
  .narrow-info-content::-webkit-scrollbar-thumb
  {
    background-color: #ba5fde;
    border-radius: 5px;
  }
  .narrow-info-content
  {
    scrollbar-color: #ba5fde transparent;
  }
  .minion-variant.minion-crafted
  {
    color: #ba5fde;
  }
  .switch_themes_button
  {
    background-color: #8d42bf;
  }
 .switch_themes_button:hover
  {
    background-color: #ba5fde;
  }
}
