css:
@-moz-document domain("crunchyroll.com"), domain("www.crunchyroll.com") {
body, a.block-link:hover {
    background-color: #0b000e !important;
}
#template_container, .showmedia-submenu, .white-wrapper, .guestbook-sort-bar {
    background-color: #010002 !important;
}
.site-header {
    background-color: #050006;
    color: #a7a7a7 !important;
}
.header-search-form, .bb-static-smilie {
    border: 0;
}
.header-searchbox, input[type="text"], input[type="password"], select, textarea {
    background-color: #2d1c33 !important;
    border: 0 !important;
    color: #a7a7a7 !important;
}
.games, .store {
    background-color: #929292 !important;
}
.site-header * {
    color: #a7a7a7 !important;
}
.header-menubar > ul > li > a:hover, #header_try_premium_free:hover span {
    color: #fff !important;
}
.new-template-body .block-link:hover, .new-template-body .pagination a, .new-template-body .para-div a, .new-template-body .sub-tabs .selectors a.text-link, .new-template-body .text-link, .new-template-body .wrapper a:hover a, .new-template-body a:hover h4, .new-template-body li a, .new-template-body p a, .new-template-body span a {
    color: #c74794;
}
.new-template-body {
    color: #929292;
}
.series-title, .collection-carousel-overlay-top {
    color: #888;
}
}
