@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("itweet.net") {
body{
background: #4088CF !important;
margin-top: 10px !important;
}
.twText{
direction:rtl !important;
font-family: Tahoma !important;
font-size: 11px !important;
padding-top: 3px !important;
}
.postField{
direction:rtl !important;
text-align: right !important;
font-family: Tahoma !important;
font-size: 11px !important;
}
div.row{
min-height: 80px !important;
border-color: #555555 !important;
background: #EDEDED !important;
-moz-border-radius: 8px !important;
-webkit-border-radius: 8px !important;
}
div.row:hover{
background: #BFBFBF !important;
}
.profileLastTweet{
direction: rtl !important;
font-family: Tahoma !important;
font-size: 11px !important;
}
div.profileTweetTime{
direction:ltr !important;
text-align: left !important;
}
.twName, .profileScreenName{
color: #003A74 !important;
}
#tweets{
background: #BBBBBB !important;
border-color: #555555 !important;
-moz-border-radius: 3px !important;
-webkit-border-radius: 3px !important;
margin-bottom: 5px !important;
}
.tweetBio, .profile, #twitterContainer, .colR{
background: #EDEDED !important;
}
.twTimeNum{
font-style: normal !important;
}
#twPostBtn{
font: normal 11px tahoma !important;
}
.profileFullName, .profileLocation, .external{
font: normal 12px tahoma !important;
}
.profileScreenName{
font: bold 16px 'Arial' !important;
}
#footer .external img{
display: none;
}
.colR{
-moz-border-radius: 7px !important;
-webkit-border-radius: 7px !important;
}
#twitterContainer, #twLogin{
-moz-border-radius: 7px !important;
-webkit-border-radius: 7px !important;
border: 1px solid #555555 !important;
}
#pagination{
-moz-border-radius: 7px !important;
-webkit-border-radius: 7px !important;
border: 1px solid #555555 !important;
background:#BBBBBB !important;
}
.replyTweet{
-moz-border-radius: 7px !important;
-webkit-border-radius: 7px !important;
border: 1px solid #555555 !important;
background:#BBBBBB !important;
margin-top: 2px !important;
direction:rtl !important;
font-family: Tahoma !important;
font-size: 10px !important;
}
}
