css:
@-moz-document url-prefix("https://login.plurall.net/login") {
/*plurall login dark  by pedro alexis*/
.p2ZbV {
    background: #e55aa7cf;
}
body {
    color: #333;
    background: #000;
}

@media screen and (prefers-color-scheme: dark) {
    body {
        background: #2d3239;
        color: #75715e;
    }

    title {
        color: #e9d970;
    }
}
body,
html {
    margin: 0;
    padding: 0;
    background: #655aa3;
    font-family: Nunito, "Open Sans", sans-serif;
    outline: 0;
}
.sub-header {
    background-color: #ee1414;
    min-height: 95px;
}
.card[_ngcontent-c3] {
    width: 90%;
    height: 100px;
    border-radius: 60px;
    -webkit-box-shadow: 0 2px 5px 0 rgba(0, 0, 0, .2);
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, .2);
    background-color: #000;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    padding: 24px 10px;
    cursor: pointer;
    margin: 24px auto;
}
.input[_ngcontent-c0] {
    margin-top: 10px;
    background-color: #000000;
    color: #000000;
    -webkit-box-shadow: #000000 1px 2px 1px inset;
    box-shadow: #000003 1px 2px 1px inset;
    font-size: 14px;
    font-family: "Open Sans", sans-serif;
    font-weight: 400;
    height: 40px;
    width: 100%;
    display: inline-block;
    border-radius: 50px;
    padding: 0 15px;
    outline: 0;
    -o-border-image: initial;
    border-image: initial;
    border: 6px solid rgba(0, 0, 0, .96);
}
}
