css:
/* i really want this to be global */

:root {
	--background: #151515;
	--text: #e0e0e0;
	--variable: #6a9fb5;
	--attribute: #00c;
	--definition: #d28445;
	--keyword: #ac4142;
	--operator: #fff;
	--property: #90a959;
	--number: #aa759f;
	--string: #f4bf75;
	--comment: #8f5536;
	--meta: #555;
	--tag: #ac4142;
	--primary: #e96802; /*#0e7f71;*/ /*#8d6a05;*/
}

body {
	max-width: 960px;
	margin: 0 auto;
}

td[bgcolor]{
	background-color: var(--primary) !important;
}

::selection {
	background: var(--primary);
	color: #fef8e8;
}

:focus {
	outline: none;
	box-shadow: 2px 2px 4px var(--primary);
}

textarea, input {
	padding: 5px 10px;
}

input[type='submit']{
	background: var(--primary);
	color: #fef8e8;
}

*, td {
	font-family: 'Fira Code';
}

#hnmain > tbody > tr:nth-child(1) > td > table img {
	display: none;
}

body {
	background-color: var(--background) !important;
}

.hnmore a:link, a:visited {
    color: #706f6f;
}

body > center > table,
input,
textarea {
	background-color: var(--background) !important;
}

body > center > table > tbody > tr:first-child > td {
	background-color: var(--primary) !important;
}

/* Bright text */
td.title a:link,
span.comment font,
span.comment font a:link,
u a:link,
span.yclinks a:link,
body:not([id]),
td:nth-child(2):not(.subtext) > a:link,
input,
textarea,
p > a,
a > u,
.c00,
.c00 a:link {
	color: var(--primary) !important;
}

a[href="https://www.ycombinator.com/apply/"] {
	display: none;
}

.admin td {
	color: #aaa !important;
}

/* search box and comment box */
input,
textarea {
	border: 1px solid var(--primary) !important;
}
