css:
@-moz-document domain("sozcu.com.tr") {
/* Reklamlar */
.sticky-object,
.pageskin-sol,
.pageskin-sag,
.sozcu_pageskin_tepe-container,
.sticky-done,
.sidebar-banner,
.outof-news-body,
.contentInnerAdv,
.reklam_sozcumain_astroloj_alti-container,
.section-right,
.left-ad-container,
.right-ad-container,
.sozcu_mast_head-container,
.news-content-right {
	display: none;
}

.section-left {
	padding-right: 0px;
}

.news-content-left {
	width: 100%;
	margin-right: 0px;
}
.content-wrapper {
	width: 950px;
}

/* Anti Adblock :) */
body,
.home {
	overflow: scroll!important;
}

.fc-ab-root .fc-dialog-overlay,
.fc-whitelist-root .fc-dialog-overlay,
.fc-ab-root .fc-dialog-container,
div.fc-ab-root,
.fc-ab-root div,
.fc-ab-root header,
.fc-ab-root h1,
.fc-ab-root h2,
.fc-ab-root a,
.fc-ab-root a:link,
.fc-ab-root p,
.fc-ab-root button,
.fc-ab-root i,
div.fc-whitelist-root,
.fc-whitelist-root div,
.fc-whitelist-root iframe {
	display: none !important;
}
}
