/* ==UserStyle==
@name           www.hurriyet.com.tr - 14/11/2020
@namespace      github.com/openstyles/stylus
@version        1.1.0
@description    A new userstyle
@author         Ender Topaloğlu
==/UserStyle== */
@-moz-document domain("hurriyet.com.tr") {
	.header-masthead,
	.premium-popup-container,
	.topBandBanner,
	#mp-widget,
	.adbl-popup-container
	{
		display: none !important;
	}

	#retouch-main-slider .swiper-pagination {
		padding: 10px;
	}

	#retouch-main-slider .swiper-pagination-bullet {
		height: auto;
		line-height: normal;
		margin: 0 2px 0 0 !important;
		width: 38.1px;
		border-radius: 5px;
		padding: 5px 0;
	}

	#retouch-flash-slider .swiper-pagination .swiper-pagination-bullet {
		border-radius: 5px;
		height: auto;
		padding: 5px;
		width: auto;
	}

	[data-track-id="flash-ust-manset"] {
		margin-bottom: 0 !important;
		margin-top: 10px;
	}

	.pageskin-fix-control {
		margin-bottom: 90px !important;
	}
}
