css:
@-moz-document domain("orteil.dashnet.org") {
::-webkit-scrollbar { width: 15px;  height: 5px;}
::-webkit-scrollbar-track { background: #1f0b04c7; }
::-webkit-scrollbar-thumb { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelVertical.png?v=2); 
}
::-webkit-scrollbar-thumb:horizontal { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelHorizontal.png?v=2); 
}
::-webkit-scrollbar-thumb:hover { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelVertical.png?v=2);
  filter: brightness(.8);
}
::-webkit-scrollbar-thumb:horizontal { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelHorizontal.png?v=2); 
  filter: brightness(.8);
}
::-webkit-scrollbar-thumb
{
  border:none;
}
#sectionRight
{
  right: -320px;
  background-image: url(https://orteil.dashnet.org/cookieclicker/img/panelVertical.png?v=2);
  background-color: #1f0b04c7;
    background-repeat: repeat-y;
    background-origin: padding-box;
    padding-left: 15px;
  
  transition: right .1s;
  
  width: 320px;
  
  z-index: 10000;
}

.storeSection
{
  width: 320px;
}
.product
{
  width: 100%;
  background-size: cover !important;
    background-position: 0px 135px !important;
}

.product .content .owned
{
  right: 20px;
}

.separatorRight
{
  right: 0;
  opacity: 0;
}

#sectionMiddle
{
  width: calc(69% - 20px);
}

#sectionRight:hover, .separatorRight:hover ~ #sectionRight
{
  right: -15px;
  
  transition: right .1s;
}

#tooltip, #tooltip > div
{
  min-width: 250px !important;
  width: 250px !important;
}
#tooltip > div
{
  padding: 0 10px 0px 0px !important;
}

/* Enlarged Left Side */

#sectionLeft
{
  width: 40%;
}
.separatorLeft
{
  left: 40%;
}
#sectionMiddle
{
  left: 40%; 
  width: calc(59% - 20px);
  margin: 0;
}
}
