css:
@-moz-document url-prefix("https://orteil.dashnet.org/cookieclicker/") {
::-webkit-scrollbar { width: 15px;  height: 5px;}
::-webkit-scrollbar-track { background: #1f0b04c7; }
::-webkit-scrollbar-thumb { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelVertical.png?v=2); 
}
::-webkit-scrollbar-thumb:horizontal { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelHorizontal.png?v=2); 
}
::-webkit-scrollbar-thumb:hover { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelVertical.png?v=2);
  filter: brightness(.8);
}
::-webkit-scrollbar-thumb:horizontal { 
  background: url(https://orteil.dashnet.org/cookieclicker/img/panelHorizontal.png?v=2); 
  filter: brightness(.8);
}
::-webkit-scrollbar-thumb
{
  border:none;
}

#tooltipAnchor
{
  left: 7% !important;
}

#tooltip, #tooltip > div
{
  min-width: 250px !important;
  width: 250px !important;
}
#tooltip > div
{
  padding: 0 10px 0px 0px !important;
}

#specialPopup
{
  z-index: 10000;  
}

/* Stack */

#sectionLeft
{
  width: 40%;
  z-index: 10000000;
}
.separatorLeft
{
  left: 40%;
  z-index: 1002;
}
.separatorRight
{
  transform: rotate(90deg);
  top: 1%;
  left: 81%;
  z-index: 1001;
  
}
#sectionMiddle
{
  left: 40%; 
  width: calc(60.5% - 20px);
  margin: 0;
  height: 50%;
}

#sectionRight
{
  opacity: 1;
  
  left: calc(40% + 15px); 
  height: 50% !important;
  
  position: relative;
  bottom: 0px !important;
  
  top: 51%;
  width: calc(60.5% - 20px);
  z-index: 10000 !important;
  }

#store > div,  .storePre, .product
{
  width: 100% !important;

  overflow-x: hidden !important;
}
.product
{
    background-repeat: repeat-y;
    background-size: 100% 400%;}
}
