css:
@-moz-document domain("multra.ga"), domain("multra.xyz") {
div.pageContent,
html,
body {
	background-color: #36393f;
	color: #dadada
}
div.jumbotron {
	background-color: #32353b;
	color: #adadad
}
/* 
i.fa fa-fw *::before {
	color: #fff !important
} */
h1,
h2 {
	color: #fff
}
h3,
h4,
h5,
h6 {
	color: #b9bbbe
}
.btn-primary {
	background: #7289da;
	color: #fff
}
.btn-secondary {
	background: #202225;
	color: #ccc
}
.bg-primary {
	background-color: #202225 !important;
}
.navbar-dark .navbar-nav .nav-link {
	color: #ccc
}
/* for home page */
#navbar01 > ul:nth-child(2) > li > div > h6 {
	color: lime !important
}
.btn-lg,
.btn-group-lg > .btn {
	padding: 1rem
}
.display-3 {
	font-size: 2rem
}
/* body > div.pageContent > div > h1:after {
	content: " - Select A Server"
} FIXME: effects all elements with the "display-3" tag outside of the dashboard page*/
.img-thumbnail {
	border-radius: 50%
}
.custom-control-input:checked,
.custom-control-input::after,
.custom-control-input:checked ~ .custom-control-label::before {
	background-color: lime
}
.custom-switch .custom-control-label::before {
	background-color: red
}
label {
	font-size: 1.2rem
}
#liveMemberCountChannel,
#modLogChannel,
#prefix,
#roleUponJoin,
#dashboardLoggingChannel,
#wrongPrefixNotifications,
#countingChannel {
	/* this system of selection is NOT ideal, but there's no other identifiers. */
	background-color: #202225;
	color: #fff;
	background-image: none
}
/* 
Author Note: So this isn't how you use CSS. You shouldn't use it to change the text on the page, however this feels
			 like its needed.
*/
body > div.pageContent > div > form > button {
	visibility: hidden
}
body > div.pageContent > div > form > button:after {
	content: 'Save Changes';
	visibility: visible;
	background: inherit;
	padding: inherit;
	border: inherit;
	margin: inherit
}
/* Stats Page */
body > div.pageContent > div > div > div * {
	color: white
}
body > div.pageContent > div > div > div > div > div:nth-child(5) > div:nth-child(2) > strong {
	/* Yoinked from discord */
	width: auto;
	height: auto;
	padding: .2em;
	margin: -.2em 0;
	border-radius: 3px;
	font-size: 85%;
	font-family: Consolas, Andale Mono WT, Andale Mono, Lucida Console, Lucida Sans Typewriter, DejaVu Sans Mono, Bitstream Vera Sans Mono, Liberation Mono, Nimbus Mono L, Monaco, Courier New, Courier, monospace;
	text-indent: 0;
	border: none;
	white-space: pre-wrap;
	background-color: #2f3136;
	color: #ccc
}
/* Commands Page */
div.card-body {
	background-color: #99AAB5;
	color: #dadada
}
h6.text-muted {
	color: #ccc !important
}

.modal-content{
	background-color: #36393f;
	color: #e4e4e5
}

#navbar01 > ul:nth-child(2) > li > div {
	background-color: #7289da
}

body > div.pageContent > div > ul > li > img {
	border: 2px solid black
}
#navbar01 > ul.navbar-nav.mr-auto > li:nth-child(3) > button {
	color: white;
	background-color: #2ecc71
}

#navbar01 > ul:nth-child(2) > li > a {
	vertical-align: baseline
}
body > div.pageContent > div > div.jumbotron > div:nth-child(1) > div > div:nth-child(3) > i.fa.fa-android.fa-fw {
	color: #7289da
}
}
