css:
@-moz-document domain("wired.com") {
html,body{
    background: #181818 !important;
    color: #aaa;
}
a,h1,h2,h3,h4,h5,p,span,label{
    color: #aaa !important;
}
a:hover{
    color: #5e5d5d !important;
}
*{
    box-shadow: none !important;
}
input,select{
    background: #181818 !important;
    color: #aaa !important;
    border: 1px solid #aaa !important;
}
/*HEADER*/
.card-component, .post-listing-component__wrapper, .subscribe-panel-component, .subscribe-panel-component__cm, .subscribe-panel-component__social, .subscribe-panel-component__newsletter, .we-recommend-component, .comments-component, .review-main-component .affiliate-links-component, .video-playlist-component__sidebar-column, .card, .page__site-footer {
    background: #282828 !important;
    border: 1px solid #444 !important;
}

hr.marg-t-50, .site-container hr {
    background: #444 !important;
}

.post-listing-component__wrapper .post-listing-list-item__post, .link--arrow, .panel-component__content, .border-t {
    border-top: 1px solid #444 !important;
}

.title-bar-component, .video-playlist-component__header {
    border-bottom: 1px solid #444 !important;
}

.panel-component--newsletter {
    border-left: 0px !important;
    border-right: 0px !important;
}

.post-listing-component__wrapper .post-listing-list-item__post:first-child, .link-banner--standard-navigation {
    border-top: 0 !important;
}

.video-playlist-component__sidebar-elements {
    border-bottom: 1px solid transparent !important;
}

.card-component h2, .post-listing-component__wrapper .post-listing-list-item__title--featured {
    background: transparent !important;
    color: #ccc !important;
}

.brow-component, .post-listing-component__wrapper .post-listing-list-item__byline, .byline-component, .recirc-most-popular__summary-item .rubric.summary-item__rubric:not(.rubric--with-bg), .standard-navigation-more-dropdown .standard-navigation-more-dropdown__label {
    color: #ccc !important;
}

.video-playlist-active-video-component__description, span, p, .caption-component__caption,.caption-component__credit {
    color: #aaa !important;
}

.header, #global-header, .logo-bar--default, #logo-bar, .header--collapsed .logo-bar, .logo-bar--default .logo-bar, .video-playlist-sidebar-item-component .ui-play, #nav-sections-wrap, .standard-navigation {
    background-color: rgba(0,0,0,.75) !important;
}

.header.header--default:not(.header--collapsed), #global-header, .recirc-most-popular__heading {
    border-bottom: 1px solid #ccc !important;
}

.recirc-most-popular__heading {
    border-top: 1px solid #ccc !important;
}

.nav--business {
    border-left: 1px solid #ccc !important;
}

.card-component:hover h2 {
    opacity: 1 !important;
}

a {
 color: #ccc !important;
}

a:active,a:hover {
 color:#eee !important;
}

h1,h2,h3,h4,h5,h6 {
 color:#ccc !important;
}

.video-playlist-component {
    background-color: #282828 !important;
    border: 1px solid #444 !important;
}

hr {
 background:rgba(255,255,255,0.13) !important;
}

code,kbd,pre,samp, .newsletter-sign-up-component__email-input {
 background:#050505 !important;
 color:rgba(255,255,255,0.8) !important;
    border: 1px solid #111 !important;
}

.logo--header, .logo--footer .icon, .ui-logo-center, .standard-navigation__drawer-toggle svg, .standard-navigation__section--logo .standard-navigation__logo-link .standard-navigation__logo-image .responsive-image__image{
    opacity: 1 !important;
    filter: invert(1) !important;
}

.footer, .paywall-container-component.paywall-container-component--growler, .related-reviews-component, .paywall-container-component .half-barrier-container .close-container, footer {
    background-color: #282828 !important;
}

.caption-component .icon {
    filter: invert(1);
}

.paywall-container-component.paywall-container-component--growler, .paywall-container-component .half-barrier-container, .paywall-bar {
    display: none !important;
}

.article-body-component a, .review-main-component .affiliate-links-component a, .footer__legal p a, .link-underline a, .link-underline-micro a {
    border-bottom: 3px solid #555;
    -webkit-box-shadow: none;
    box-shadow: none;
    transition: all .15s;
}

.article-body-component a:hover, .review-main-component .affiliate-links-component a:hover,.footer__legal p a:hover, .link-underline a:hover, .link-underline-micro a:hover {
    background-color: #555;
    -webkit-box-shadow: inset 0 -4px 0 #555;
    box-shadow: inset 0 -4px 0 #555;
}
/*HEADER*/
/*MAINPAGE*/
.wallpaper-component,.video-playlist-component,.post-listing-component__wrapper .post-listing-list-item__title--featured,.subscribe-panel-component__cm, .subscribe-panel-component__social, .subscribe-panel-component__newsletter,.app-shell-component--homepage{
    background-color: #181818 !important;
}
.card-component,.title-bar-component,.post-listing-component__list,.link--arrow{
    background-color: #181818;
    border: 1px solid #aaa;
}
.card-component h2{
    background-color: #181818;
    color: #aaa;
}
.post-listing-component__wrapper .post-listing-list-item__link:hover .post-listing-list-item__title{
    color: #5e5d5d !important;
}
/*MAINPAGE*/
/*ARTICLE*/
.content-header__row {
    background: #181818 !important;
}
.standard-navigation,main,.page--article, .link-banner--standard-navigation{
    background-color: #181818;
}
.standard-navigation__logo-link .responsive-image__image{
    filter: invert();
}
.standard-navigation__drawer-toggle svg path,.standard-navigation__search svg path{
    fill: #aaa;
}
.drawer{
    background: #0000008f;
    border-right: 1px solid #aaa;
}
.content-header__dek,.content-header__dek:hover,.content-header__publish-date,.body ul li,.content-footer .content-footer-related-video .content-footer-related-video-dek,.body .heading-h1, .body .heading-h2, .body .heading-h3, .body .heading-h4, .body .heading-h5, .body .heading-h6{
    color: #aaa;
}
.recirc-most-popular__heading{
    border-color: #aaa;
}
/*ARTICLE*/
/*SUBPAGES*/
.cm-subcard .card-failsafe{
    background-color: #181818;
}
/*AUTHOR*/
.contributor-main-component{
    background-color: #181818;
}
.archive-item-component__byline{
    color: #aaa;
}
/*AUTHOR*/
/*LOGIN*/

.line-break > span,.base-account-page{
    background-color: #181818;
}
/*LOGIN*/
/*SUBSCIPTIONS*/
.panel-component--subscribe-page .panel-component__header,.panel-component--subscribe-page .panel-component__content,.page-wrapper--subscribe,.main-nav .navbar-default .navbar-nav > li > a.active,.covers,#pagesection,.panel-body,.panel-default > .panel-heading{
    background-color: #181818;
}
.subscribe-main-component__button a,#order-progress-bar,.panel.offer.off,.nextgen-pp-gift-payment{
    background-color: #181818;
    border: 1px solid #aaa;
}
.main-nav .navbar-default{
    background-color: #181818;
    border: 1px solid #aaa;
    border-bottom: 0;
}
.form{
    background: #181818;
    border: 1px solid #aaa;
    border-top: 0;
}
#digital_auth_ext .form{
    border-top: 1px solid #aaa;
}
.jumbotron{
    background-color: #181818;
    border-bottom: 1px solid #aaa;
}
#cart{
    filter: invert();
}
.btn-custom:hover{
    background: #aaa !important;
    color: #181818 !important;
}
.panel.offer.off,#privacytext, #giftprivacytext, #impulse-giftprivacytext,#cctext,.ccinfo,#itemsummarydesc, #impulseummarydesc,#itemprice, #impulseprice,#shipcost,#total{
    color: #aaa;
}
.panel.offer.on{
    color: #057dbc
}
button{
    background: #181818;
    border: 2px solid #aaa;
    color: #aaa;
}
button:hover{
    background: #aaa;
    color: #181818;
    cursor: pointer;
}
/*SUBSCIPTIONS*/
/*ADVERTISE*/
.bg-white{
    background-color: #181818;
}
.ui-logo-left,.ui-boxes,.ui-search{
    filter: invert();
}
.subscribe-cta{
    color: #aaa;
}
/*ADVERTISE*/
/*SITEMAP*/
.card{
    background-color: #181818;
    border: 1px solid #aaa;
}
.card.smart{
    border-top: 1px solid #aaa;;
}
/*SITEMAP*/
/*MOST-POPULAR*/
.page-wrapper--most-popular .most-popular-listing-main-component{
    background-color: #181818;
}
/*MOST-POPULAR*/
/*NEWSLETTER*/
.newsletter-manage__newsletter-subscribe-form,.newsletter-manage{
    background-color: #181818;
}
/*NEWSLETTER*/
/*STAFF*/
.wired-staff-single-component__name{
    color: #aaa;
}
/*STAFF*/
/*COUPONS*/
.m-navigation, .topbar,.head-disclaimer,.featured-shops, .m-search-box, body,.home .headline,.hero__link, .hero__links,.w-featured__frame,.premium-widget,.modal-clickout__code-wrp,.modal-clickout__footer{
    background-color: #181818 !important;
}
.new-disclaimer{
    background-color: #181818;
    color: #aaa;
}
.home .w-featured__list--33 .w-featured__headline,.premium-offer__body,.premium-offer.premium-offer--33-colored-full-bg::after,.seo-text__card,.modal__dialog,.scroll-to-top{
    background: #181818;
    border: 1px solid #aaa;
}
.w-featured__item{
    border-left: 1px solid #aaa;
    border-right: 1px solid #aaa;
}
.home .w-featured__more-link{
    background: #181818;
    border-left: 1px solid #aaa;
    border-right: 1px solid #aaa;
}
.w-featured__content,.premium-offer.premium-offer--100 .premium-offer__headline,.premium-offer.premium-offer--100 .premium-offer__description,.premium-offer.premium-offer--33-colored-full-bg .premium-offer__description{
    color: #aaa;
}
.modal-clickout__code{
    color: #d73421 !important;
    border-color: #181818;
}
/*COUPONS*/
/*SUBPAGES*/
/*FOOTER*/
.more-results-component,.footer{
    background-color: #181818;
}
.footer .logo_dark,.ui-logo-center{
    filter: invert();
}
footer:after,#footcopyright:after{
    content: "Dark theme by Maciejozaman";
    color: #aaa;
}
.content-footer-related-video-header, .content-footer-related-video-title, .section-title__hed, .content-footer-related-video-dek, .sc-ensa-Dq, .sc-bwcZQD
{
    color: #aaa !important;
}
/*FOOTER*/
/*CARDS*/
.content-card-embed__dek
{
    color: #aaa !important;
}
.product-card, .product-card__name, .sc-bdnylx.sc-gtssRu.eUOjDh.klXntL.button.button--primary-pair.product-offer__buy-button.button--text-inside, .article__series-navigation {
	color: #aaa !important;
	background-color: #181818;
}
/*CARDS*/
}
