css:
@-moz-document url-prefix("https://qresear.ch/"), url-prefix("https://www.resignation.info/"), url-prefix("https://deathcas.es/"), url-prefix("https://qposts.in/") {
body {
	background: #18181A;
	color: whitesmoke;
}

a.link {
	color: #85bbff;
}

a,
a:visited {
	color: #85bbff;
}

input[type="text"],
input[type="password"],
input[type="email"],
textarea,
select {
	background: transparent;
	color: white;
}

/* sections */
#navigation {
	background-color: #38383D;
}

#wrapper {
	background: #232327;
}

#content {
	background: #232327;
}

#content h1,
h2,
h3,
h4,
h5 {
	color: whitesmoke;
}

#footer {
	background: #18181A;
}

#footer-bottom {
	background: #232327;
}

/* accordions */
.accordion {
	background: #18181A;
}

.ui-accordion .ui-accordion-content {
	border: 1px solid #36ff8f;
	border-top: 0;
}

.ui-accordion .ui-accordion-header {
	background-color: #38383D;
}

.ui-accordion .ui-accordion-header:hover {
	background-color: #232327;
}

.ui-accordion .ui-accordion-icons {
	color: #85bbff;
}

.ui-accordion-icon {
	background-color: #1f1f1f;
	border: solid 1px whitesmoke;
}

/* pagination */
.pagination ul li a {
	background-color: #18181A;
}

.pagination ul li a:hover {
	background-color: #38383D;
}

/* notices */
.large-notice {
	background: #18181A;
	border: solid 1px #85bbff;
}

.large-notice p {
	color: white;
}

.notification.warning {
	background-color: #18181A;
	color: white;
	border: 1px dashed #f2ff61;
}

.notification.error {
	background-color: #18181A;
	color: white;
	border: 1px dashed #e74c3c;
}

.notification.success {
	background-color: #18181A;
	color: white;
	border: 1px dashed #36ff8f;
}

/* q posts page  */
.item-description {
	background: #38383D;
}

table.standard-table tr:hover td {
	background: #232327;
}

/* individual q posts */
table.standard-table th {
	color: whitesmoke;
	background-color: #232327;
}

.post_head a,
.post_head a:hover,
.post_head a:visited {
	color: #85bbff;
}

.post_head b {
	color: #ccc;
}
}
