css:
@-moz-document domain("aulas3.uach.mx") {
span {
  color: #c5cad2 !important;
}

body {
  background: #21252b !important;
  color: #c5cad2 !important;
}

h1,
h2,
h3,
h5{
  color: #c5cad2 !important;
}

#wrapper {
  border-top: 0px transparent !important;
  background: #282c34 !important;
}

#page-header,
.navbar,
#page {
  background: #282c34 !important;
}

.logo {
  filter: invert(90%) !important;
}

#inputName,
#user,
#pass,
#inputPassword {
  background: #1c1e24 !important;
  border: none !important;
}

.navbar-inner,
.title,
.tab-pane,
.well,
.block {
  background: #2c323c !important;
  border-color: #6fb1d3 !important;
}

button,
.brand,
.btn,
.btn-primary,
.nav-item.active a,
input[type=submit],
#sidebar-btn,
.bx-prev,
.bx-next,
#page-footer .socials .social_icons a.social,
.navbar .nav li a:hover,
.navbar .nav li a:focus,
.navbar .dropdown-menu li a:hover,
.nav-tabs .active a,
.section-navigation a::after {
  background-color: #4fa1c9 !important;
  color: white !important;
}

.nav-item a,
.nav-tabs a {
  background-color: transparent !important;
}

a,
.tree_item.branch span,
h2::before,
#loggedin-user .usertext,
span.instancename,
a span {
  color: #6fb1d3 !important;
}

#sidebar .block-region,
.block_navigation {
  background: #2c313a !important;
  border: 0px transparent !important;
}

.camera_wrap {
  display: none !important;
}

.muted {
  color: #728097 !important;
}

.generaltable tbody tr:nth-child(odd) td,
.generaltable tbody tr:nth-child(odd) th {
  background: #2a2e37 !important;
}

.generaltable td,
.generaltable th {
  border-top: 1px solid #404754 !important;
}

.generaltable tbody tr td,
.generaltable tbody tr:nth-child(even) th {
  background: #282c34 !important;
}

td {
  color: #c5cad2 !important;
}

.generaltable tbody tr:hover td,
.generaltable tbody tr:hover th {
  background: #2f333d !important;
}

#page-footer {
  border-top: 3px solid #547d92 !important;
}

.breadcrumb span {
  color: #96c6df !important;
  text-shadow: none !important;
}

.navbar .dropdown-menu {
  border-bottom: 4px solid #6fb1d3 !important;
}

.nav-tabs {
  border-bottom: 2px solid #6fb1d3 !important;
}

.calendar_event_course {
  background-color: rgba(255, 255, 255, .05) !important;
}

.minicalendar.calendartable .calendar-controls {
  background: transparent !important;
}

button.btn.btn-link,
.section .activity .actions {
  background-color: transparent !important;
}

#page-content aside img {
  width: auto !important;
}

.que .info,
.que .formulation {
  background-color: #2c323c !important;
  border: none !important;
  color: #c5cad2 !important;
  text-shadow: none !important;
}

.formulation .correct {
  background-color: #405837 !important;
}

.que .outcome {
  background-color: #4c5667 !important;
  color: #c5cad2 !important;
  text-shadow: none !important;
  border: none !important;
}

.path-mod-quiz #mod_quiz_navblock .allquestionsononepage .qnbutton.thispage .thispageholder,
.path-mod-quiz #mod_quiz_navblock .qnbutton .thispageholder {
  border: none !important;
}

span.answer input[type="text"] {
  background-color: #202634 !important;
  border: 1px solid #54bef2 !important;
  color: #d0d3da !important;
}

.path-mod-quiz #mod_quiz_navblock .qnbutton.notyetanswered .trafficlight,
.path-mod-quiz #mod_quiz_navblock .qnbutton.invalidanswer .trafficlight {
  background-color: rgba(255, 255, 255, 0.02) !important;
  border: none !important;
}

.path-mod-quiz #mod_quiz_navblock .qnbutton.answersaved .trafficlight {
  background-color: rgba(255, 255, 255, 0.32) !important;
  border: none !important;
}

.btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active,
.btn-primary.disabled,
.btn-primary[disabled] {
  background-color: #4fa1c9 !important;
  color: white !important;
}

a.qnbutton {
  background-color: #363e4a !important;
}

.moodle-dialogue-base .moodle-dialogue-lightbox {
  background-color: #000 !important;
}

.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
  background: transparent !important;
  text-shadow: none !important;
  border: none !important;
}

.moodle-dialogue-base .moodle-dialogue-wrap {
  background: #333842 !important;
  border: none !important;
  box-shadow: 5px 5px 20px 0 #111 !important;
}

.moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd {
  color: #c5cad2 !important;
}

button.yui3-button.closebutton {
  filter: invert(80%) !important;
  box-shadow: none !important;
}

table#form td.submit,
.form-buttons,
.path-admin .buttons,
#fitem_id_submitbutton,
.fp-content-center form + div,
div.backup-section + form,
#fgroup_id_buttonar {
  background-color: #282c34 !important;
  border: none !important;
}

input[name=cancel] {
  filter: saturate(0%) !important;
}



.filemanager .fp-navbar,
.filepicker-filelist,
.filemanager-container,
.fp-pathbar {
  background: #3d4552 !important;
  border: none !important;
}

.filemanager-toolbar {
  border-bottom: 2px solid #282c34 !important;
}

.dndupload-arrow {
  background: url("data:image/svg+xml;base64,") center no-repeat !important;
  background-size: contain !important;
  filter: invert(100%) !important;
}

.fp-navbar {
  border-top-left-radius: 16px !important;
  border-top-RIGHT-radius: 16px !important;
}

.filemanager-container {
  border-bottom-left-radius: 16px !important;
  border-bottom-right-radius: 16px !important;
}

.fm-empty-container {
  border-radius: 16px !important;
}

.filemanager-toolbar .fp-btn-add,
.filemanager-toolbar .fp-btn-mkdir,
.filemanager-toolbar .fp-btn-download,
.filemanager-toolbar .fp-viewbar {
  background-color: transparent !important;
  border: none !important;
}

.filemanager-toolbar a[role=button] img.icon,
.filemanager-toolbar .fp-viewbar a.checked img.icon,
.filemanager-toolbar .fp-viewbar a:active img.icon {
  filter: brightness(0) invert() !important;
}

.filemanager-toolbar .fp-viewbar a img.icon {
  filter: brightness(0) invert(50%) !important;
}

.filemanager-toolbar .fp-viewbar a {
  border: none !important;
}

.filemanager-toolbar .fp-viewbar a.checked,
.filemanager-toolbar .fp-viewbar a:active,
.filemanager-toolbar .fp-viewbar a:hover {
  background-color: transparent !important;
  background-image: none !important;
}

.filemanager-toolbar .fp-viewbar a:hover {
  background-color: rgba(255, 255, 255, 0.1) !important;
  transition: 0.3s !important;
}

a.fp-path-folder-name,
.nav.nav-tabs li.active span {
  color: white !important;
}

.fp-pathbar .fp-path-folder {
  background: url(https://drive.google.com/uc?export=view&id=109UiOQng8RoKfdPP62X5VBG8Qd76d3w-) 0 0 no-repeat !important;
}

.filemanager-container .dndupload-target {
  background: #3d4552 !important;
  border-radius: 16px !important;
  box-shadow: none !important;
}

.dndupload-ready {
	display: block
}

.fp-iconview .fp-thumbnail img,
.fp-iconview .fp-thumbnail {
  box-shadow: none !important;
  border: none !important;
}

.fp-iconview .fp-file .fp-filename-field .fp-filename {
  color: #c5cad2 !important;
  background: none !important;
  font-size: 13px !important;
  padding: 0 !important;
}

.fp-iconview .fp-filename-field {
  overflow: visible !important;
}

::-webkit-scrollbar {
  width: 12px;
}

::-webkit-scrollbar-track {
  background: hsla(0, 0%, 100%, 0.05);
}

::-webkit-scrollbar-thumb {
  background: hsla(0, 0%, 100%, 0.26);
}

::-webkit-scrollbar-thumb:hover {
  background: hsla(0, 0%, 100%, 0.63);
}

.welcome_userpicture {
  border-radius: 8px !important;
}

.block_online_users ul {
  white-space: nowrap !important;
}

.block_online_users .user,
.block_online_users .user a {
  width: 90% !important;
  overflow: hidden !important;
  text-overflow: ellipsis !important;
}
.block_online_users .message,
.block_online_users .message a {
  width: 10% !important;
}

.langmenu .dropdown-menu li a {
  background-color: #282c34 !important;
  color: #96c6df !important;
  border-bottom: none !important;
  border-top-width: 1px !important;
}

.langmenu .dropdown-menu li:first-child a {
  border-top: none !important;
}
}
