css:
@-moz-document regexp(".*kibana\\..*") {
.euiComboBoxPill--plainText {
    white-space: nowrap !important;
    text-overflow: initial !important;
}

.globalFilterEditor__fieldInput {
    max-width: 550px;
}

.euiFlexItem.euiFlexItem--flexGrowZero > div:not(.euiFormRow):not(.euiPopover) {
    max-width: 900px;
    width: 700px !important;
}

.euiComboBoxOption__content {
    text-overflow: initial !important;
}

.euiFlexGroup,
.euiFlexItem {
    flex-shrink: 0;
}
}
