css:
@-moz-document url("https://humanbenchmark.com/tests/reactiontime") {
/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
 * {
    color: #FFF;
}
html {
    min-height: 100vh;
}
body {
    height: 100vh;
    overflow: hidden; /* Hide scrollbars */
}
/*  title div */
 .e15q442n2 {
    background: rgb(206, 38, 54);
}
/*  hides descriptive text, header, footer*/
 .hb-chart, .e19owgy71, .e19owgy76, .e15q442n0, .e15q442n2, .css-1l6ve91, .e19owgy74 {
    display: none;
}
/*  blue */
 .view-splash, .view-result, .view-score, .view-scold {
    background: rgb(43, 135, 209) !important;
    height: 100vh;
}
/*  red */
 .view-waiting {
    background: rgb(206, 38, 54) !important;
    height: 100vh;
              
    /* Hide the text. */
    text-indent: 100vw;
    white-space: nowrap;
    overflow: hidden;
}
/*  green */
 .view-go {
    background: rgb(75, 219, 106) !important;
    height: 100vh;
              
    /* Hide the text. */
    text-indent: 100vw;
    white-space: nowrap;
    overflow: hidden;
}
}
