css:
@-moz-document domain("tunisianet.com.tn") {
main {
    background-color: #222;
}

.topmenu {
    background-color: #353535;
}

#footer {
    background: #0a2939;
}

.footer-top {
    background: #0a2939 !important;
}

@media (min-width: 992px) {
    .header-top {
        background-color: #3378bd;
    }
}

.form-control {
    background: #4f4f4f;
}

body {
    color: #eaeaea;
}

a {
    color: #eee;
}

p {
    color: #a8c5d9;
}

.price {
    color: #F98585 !important;
}

.wb-menu-vertical ul li.level-1 > a span,
.wb-menu-vertical ul li.level-1 .icon-drop-mobile span {
    color: #a8d3ff;
}

#header .wb-menu-vertical ul li.level-1 li.item-header a {
    color: #a3e2ff !important;
}

#search_category {
    color: #e3e3e3;
    border: 1px solid #000;
}

#search_query_top {
    border: 1px solid #000;
}

#wbsearch_content_result {
    background: #4f4f4f none repeat scroll 0 0;
    border: 1px solid #262626;
}

#close_search_query_nav::before {
    color: white;
}

#wbsearch_data .noresult {
    color: #c1c1c1;
}

.form-control:focus {
    background-color: #3e3e3e;
    color: #e6e6e6;
}

.form-control::placeholder {
    color: #ececec;
}

@media (min-width: 768px) {
    .wb-menu-vertical .menu-dropdown {
        background: #424242;
    }
    .top-menu a:not([data-depth="0"]) {
        color: #d5d5d5;
    }
}

#search_category {
    background-image: url("https://external-content.duckduckgo.com/iu/?u=http%3A%2F%2Fwww.richardpinkstone.com%2Fwp-content%2Fuploads%2F2018%2F08%2Fdown-arrow.png&f=1&nofb=1");
    background-size: 28px, 28px;
}

#wrapper .breadcrumb ol {
    border: 1px solid #606060;
    background-color: #353535;
}

.btn {
    fill: #e1e1e1;
    color: #eee;
}

.social-sharing li {
    background-color: #464545;
}

.product-quantity #quantity_wanted {
    color: #e1e1e1;
    background-color: #222;
    border-color: #424242;
}

.bootstrap-touchspin .btn-touchspin:hover {
    background-color: #222;
}

.bootstrap-touchspin .btn-touchspin {
    background-color: #222;
    border: 0px solid #eee;
}

.bootstrap-touchspin .input-group-btn-vertical {
    color: #d0d0d0;
}

.product-features > dl.data-sheet dd.value,
.product-features > dl.data-sheet dt.name {
    background: #222;
}

.product-features > dl.data-sheet dd.value:nth-of-type(2n),
.product-features > dl.data-sheet dt.name:nth-of-type(2n) {
    background: #222;
}

.resume {
    border: solid 1px #686868;
    background: #424242;
}

.product-prices div,
.product-prices .tax-shipping-delivery-label {
    color: #EC8484;
}

.label {
    color: #eee;
}

.product-tab {
    background: #424242;
}

.data-sheet .name {
    color: #eaeaea;
}

.wb-compare-wishlist-button .wish span,
.wb-compare-wishlist-button .wb-compare-button span {
    color: #c4c3c3;
    fill: #e6e6e6;
}

.btn-primary {
    border: 1px solid #535353;
    background-image: linear-gradient(#222, #222);
}

.btn-primary:hover {
    background-image: linear-gradient(#222, #222);
}

.block-reassurance-item .h6 {
    color: #a8dcf9;
}

.block-social .social li {
    background-color: #4d4d4d;
}

.block-social .social li a::before {
    color: #c7e0ee;
}

#product #block-reassurance {
    background: #484848;
}

#index #page-preloader {
    background: #464646;
}

#block-reassurance {
    background-color: #353535;
}

#products {
    background: #353535;
}

.wb-grid-list .selected svg {
    fill: #cecece;
}

.listds a {
    color: #c0d3df;
}

#products .product-reference {
    color: #e3e3e3;
}

#amazzing_filter {
    border: 1px solid #575757;
    background: #353535;
}

.pagination {
    background: #1e1e1e;
    border: 1px solid #484848;
}

.wb-product-list .thumbnail-container {
    padding-left: 20px;
}

.product-manufacturer {
    background-color: white;
    border-radius: 10px;
}

.wb-product-list .wb-image-block {
    border-radius: 10px;
}

.products-selection {
    border: 1px solid #424242;
    background-color: #1e1e1e;
}

.dropdown-menu {
    background-color: #1e1e1e;
}

.label,
label {
    color: #bfbfbf;
}

.card {
    background-color: #1e1e1e;
    border: 1px solid #404040;
}

.h1,
.h2,
.h3 {
    color: #dbdbdb;
}

.af_filter .toggle-cut-off span {
    background: #353535;
    color: #d7d7d7;
}

.owl-carousel .owl-item {
    background-color: white;
    padding: 10px;
    border-radius: 20px;
}

.thumbnail-container .wb-product-desc {
    background-color: #353535;
}

.thumbnail-container .product-title a {
    color: #b1d0ee;
}

.wb-product-grid .wb-product-desc .product-manufacturer {
    width: 80%;
    margin-left: 10%;
    margin-right: 10%;
    margin-top: 10px;
}

.wb-image-block img {
    border-radius: 10px;
}

body#product h1 {
    color: #bed6ee;
}


* {
    scrollbar-width: thin !important;
    scrollbar-color: #333 #1b1b1b;
}
}
