css:
@namespace url(http://www.w3.org/1999/xhtml);
a[hreflang]:after{
  content: " (" attr(hreflang) ")";
}

a[hreflang=""]:after,
h1>a[hreflang]:after,
h2>a[hreflang]:after,
h3>a[hreflang]:after,
a[hreflang^="fr"]:lang(fr):after,
a[hreflang^="de"]:lang(de):after,
a[hreflang^="es"]:lang(es):after,
a[hreflang^="en"]:lang(en):after{
  content:none !important;
}
