css:
@-moz-document regexp("https://goworkship.com/agreements/\\d+/work_schedule.*") {
.c-work-schedule-table
.c-work-schedule-table__head
.c-work-schedule-table__head-ttl,
.c-work-schedule-table
.c-work-schedule-table__list
.c-work-schedule-table__list-item,
.c-work-schedule-table
.c-work-schedule-table__list
.c-work-schedule-table__list-item
    .acd-content
    .acd-content__ttl
{ border-width: 0 }
.c-work-schedule-table
.c-work-schedule-table__head
.c-work-schedule-table__head-ttl:first-child
{ padding-left: 4px }
.c-work-schedule-table
.c-work-schedule-table__head
.c-work-schedule-table__head-ttl:last-child
{ padding-right: 4px }
.c-work-schedule-table
.c-work-schedule-table__list
.c-work-schedule-table__list-item
{
    padding-left: 4px;
    padding-right: 4px;
}
.c-work-schedule-table
.c-work-schedule-table__list
.c-work-schedule-table__list-item
    .acd-content
    .acd-content__ttl.memo
{ padding: .4em .5em !important }
.c-work-schedule-table
.c-work-schedule-table__list
.c-work-schedule-table__list-item
    .acd-content
    .acd-content__ttl.memo
        textarea
{
    box-sizing: border-box;
    padding: .25em .4em;
    height: 100%;
    line-height: 1.25;
}

.c-work-schedule-table input:not(.total-value),
.c-work-schedule-table textarea
{
    border-color: rgba(0 0 0 / 20%) !important;
    background: #fff; /* Only input */
}

/* Fixed Bottom Buttons */
.c-work-schedule-btn-container
{
    /* Clear width and Make under layer elements clickable */
    display: table;
    margin: auto;
    width: auto;
}
.c-work-schedule-btn-container
.c-work-schedule-btn-wrap
{
    border-radius: 8px;
    background: rgba(255 255 255 / .9)
}
.c-work-schedule-btn-container
.c-work-schedule-btn-wrap
.c-work-schedule-btn
{ transition: .2s }
.c-work-schedule-btn-container
.c-work-schedule-btn-wrap
.c-work-schedule-btn.submit:not(:hover):not(:focus)
{ background: 0 }
.c-work-schedule-btn-container
.c-work-schedule-btn-wrap
.c-work-schedule-btn:not(:hover):not(:focus)
{ opacity: .4 }
}
