css:
@-moz-document url-prefix("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe"), url-prefix("https://www.123456.com") {
body, #help-popup {
  background-color: #343434
}
#sections-heading, #heading, #mozilla-format-heading, #lint, #enabled-label, #options-heading, .style-contributor, #save-button, button, select, .warning, #lineWrapping-label, #smartIndent-label, #indentWithTabs-label, #tabSize-label, #keyMap-label, #theme-label, .applies-to, label, caption, #help-popup, tbody, .style-name, #manage-heading, #filters {
  color: #b2b2b2;
}
label {
  display: inline-block;
	margin-bottom: 5px!important;
}
#manage-text a {
  color: #b2b2b2;
}
/* 已启用 文字 */
#enabled-label {
	line-height: 24px;
  margin: 0px!important;
}
/* 一起用的多选框 */
.style-contributor {
	top: 0!important;
}
#header {
  background-color: #484848;
  border-right: 1px solid #575757
}
.title {
  background-color: #484848!important;
}
button {
  cursor:pointer;
  margin: 0;
  padding: 3px 5px;
  border: 1px solid transparent;
  outline: none;
  background-color: #616161;
  border-radius: 3px;
  transition: 0.2s;
}
button:hover {
  background-color: rgba(255,255,255,0.3)!important;
}
input, select {
  background-color: #343434;
}
input[type="checkbox"] {
	position: relative;
  top: 3px;
}
input[type="number"], input[type="search"], .applies-value {
  box-shadow:none;
  outline: none;
  resize: none;
  border: none;
  -webkit-appearance: none;
  -webkit-tap-highlight-color:rgba(0, 0, 0, 0);
  padding: 3px 5px;
  border-radius: 3px;
  border: 1px solid #575757;
  transition: 0.3s;
  color: #b2b2b2;
}
#basic-info-name input {
  box-shadow:none;
  outline: none;
  resize: none;
  border: none;
  -webkit-appearance: none;
  -webkit-tap-highlight-color:rgba(0, 0, 0, 0);
  padding: 3px 5px;
  border-radius: 3px;
  border: 1px solid #575757;
  transition: 0.3s;
  color: #b2b2b2;
}
.style-contributor:focus {
  border: rgba(178, 178, 178, 0.7) 1px solid!important;
}
select {
  border-radius: 3px;
  border: 1px solid #575757;
  outline: none;
  transition: 0.3s;
}
select:focus {
 	border: rgba(178, 178, 178, 0.7) 1px solid!important;
}
input[type="number"] {
  max-width: 70px!important;
  text-indent:10px;
  text-align: left!important;
}
input[type="number"]:focus {
	border: rgba(178, 178, 178, 0.7) 1px solid!important;
}
/* 问号的样式 */
#keyMap-help, #lint-help, #sections-help, #to-mozilla-help, .applies-to-help {
  position: relative;
  margin-left: 5px;
}
.applies-to-help {
	top: 1px;
}
#keyMap-help {
  top: 5px
}
#lint-help, #sections-help, #to-mozilla-help {
  top: 2px
}
/* 问号弹出窗口的关闭按钮 */
.close-icon {
	background: linear-gradient(-45deg, transparent 5px, #b2b2b2 5px, #b2b2b2 6px, transparent 6.5px) no-repeat, linear-gradient(45deg, transparent 5px, #b2b2b2 5px, #b2b2b2 6px, transparent 6.5px) no-repeat!important;
}
/* 右下删除按钮 */
.remove-applies-to {
  margin: 0 10px
}
#installed::after {
  background-color: #616161;
}
/* 弹窗问题俩字下面标题 */
.contents a {
	color: #f2f2f2
}


/* 代码内的样式 */

/* 代码的大盒子 */
.CodeMirror {
	border: 1px solid #575757!important;
  outline: none;
  font-family: Consolas;
}
.CodeMirror:focus {
  border: rgba(255, 255, 255, 0.7) 1px solid!important;
}
 .CodeMirror-linenumber {
  color: #858585!important;
}
.CodeMirror, .CodeMirror-gutters {
  background-color: #1e1e1e!important;
}
/* 代码高亮样式 */

/* .codemirror-line {
	background-color: #1e1e1e!important;
} */

/* 属性名字 兼容名称的颜色 */
 .cm-property, .cm-meta {
  color: #9cdcfe!important;
}
/* id名字 class名字 标签名字 */
 .cm-builtin, .cm-qualifier, .cm-tag {
  color: #d7983d!important;
}
/* 字符串颜色 */
 .cm-atom, .cm-string {
  color: #ce8349!important;
}
/* import的颜色 */
 .cm-keyword {
  color:#569cd6!important;
}
/* 数字的颜色 */
 .cm-number {
  color: #b5cea8!important;
}
/* 花括号被选中时的颜色 */
 .codemirror-matchingbracket {
  /* 	border: 1px solid #ccc;
  box-sizing:border-box; */
}
/* 标点符号的颜色 */
 span {
  color: #d4d4d4
}
/* 注释的颜色 */
 .cm-comment {
  color: #6a9955!important;
}
.code {
  color:#6a9955!important;
}
/* 报错时的颜色 */
.cm-error {
	background-color: transparent!important;
	color: red!important;
}
/* 每一行代码的样式 */
.codemirror-line  {
	font-size: 16px!important;
}
/* 滚动条的样式 */
.CodeMirror-vscrollbar {
  margin: 0px!important;
}
.CodeMirror-vscrollbar::-webkit-scrollbar {
  -webkit-box-shadow: inset 0 0 5px rgba(0,0,0,0.2);
  border-left: 1px solid #575757;
}
.CodeMirror-vscrollbar::-webkit-scrollbar-thumb {
	background-color: rgba(72,72,72,0.5);
}
.CodeMirror-vscrollbar::-webkit-scrollbar-thumb:hover {
	background-color: rgba(72,72,72,0.7);
}
.resize-grip {
	padding-left: 8px;
  border-left: 1px solid #575757;
}
#lint div::-webkit-scrollbar {
  -webkit-box-shadow: inset 0 0 5px rgba(0,0,0,0.2);
  border: 1px solid #575757;
  padding: 2px;
  background: rgba(0,0,0,0.5)!important;
}
#lint div::-webkit-scrollbar-thumb {
	background-color: rgba(30,30,30,1);
  background: rgba(72,72,72,0.7)!important;
  border: 1px solid #575757;
}
#lint div::-webkit-scrollbar-thumb:hover {
	background-color: rgba(72,72,72,1)!important;
}
#lint div::-webkit-scrollbar-corner  {
	background-color: rgba(72,72,72,1)!important;
}
}
