css:
@-moz-document domain("translate.google.com") {
header,
.frame,
.container,
.tlid-language-bar,
.swap-wrap,
.swap.jfk-button,
.language-list-search-box,
.language_list_search_box_container input,
.input-button-container,
.language-list,
.language_list_languages,
.language_list_section,
.language_list_item_wrapper,
.select-file-page,
.language-list-toolbar,
.orig,
#spelling-correction,
#input-wrap,
.source-wrap{
    background-color: #292929 !important;
}

.ls-wrap {
    border-bottom: 1px solid white;
}

.results-container,
.result {
    background-color: #1e1e1e !important;
}

.results-container:not(.empty) .placeholder,
.result-dict-wrapper .translation,
.contribute-target,
#st-buttons .jfk-button,
.language_list_search_box_container input,
.language_list_item,
.select-file-section .choose-document-prompt,
.select-file-section .upload-filetypes-prompt,
.language-list-toolbar .language-list-label,
.input-button .text,
.orig,
#spelling-correction,
#input-wrap,
.source-wrap, 
.results-container,
.result,
.ft-icon-txt,
.gb_0c, 
.jfk-button-standard{
    color: #cecece !important;
}


.homepage.translate-text .input-button.text-icon,
.homepage.translate-docs .input-button.documents-icon {
    background-color: rgba(215, 215, 215, 0.12) !important;
    box-shadow: 0px 0px 5px #d7d7d7;
}

.ls-wrap .sugg-fade {
    background: linear-gradient(to right, rgba(255, 255, 255, 0), rgb(30, 30, 30)) !important;
    background: -o-linear-gradient(right, rgba(255, 255, 255, 0), rgb(30, 30, 30)) !important;
    background: -moz-linear-gradient(right, rgba(255, 255, 255, 0), rgb(30, 30, 30)) !important;
    background: -webkit-linear-gradient(left, rgba(255, 255, 255, 0), rgb(30, 30, 30)) !important;
}


.ls-wrap .sl-sugg .goog-inline-block.jfk-button:focus,
.ls-wrap .tl-sugg .goog-inline-block.jfk-button:focus,
.ls-wrap .sl-more:focus,
.ls-wrap .tl-more:focus,
.ls-wrap .lang-btn:focus-within,
.ls-wrap .swap:focus,
.language-picker-toolbar-image:focus {
    background: #292929 !important;
    outline-width: 0 !important;
}

.language_list_item_wrapper:hover,
.language_list_item_wrapper:focus {
    background: #3e3e3e !important;
}
}
