css:
@-moz-document domain(sky.shiiyu.moe){
a {
    color:  #d72626; 
}

.external-app-name {
    color: #e72421;
}

#goto_target_username {
    background-color: rgb(198, 36, 44);
}

#goto_target_username:hover {
    background-color: rgb(245, 14, 14);
}

.additional-player-stat {
    background-color: rgb(225, 37, 46);
}
.additional-player-stat:hover {
    background-color: #da0e18;
}

.skill-progress-bar {
    background-color: #e83030;
}

.skill-icon {
    background-color: #e83030;
}

.skill-bar::before {
    background-color: #e83030;
}

#nav_bar_line {
    background-color: #e61106;
}

.nav-item::after {
    background-color: #d51d13;
}

.stat-header span {
    border-bottom: 4px solid #d51613;
}

.inventory-header-line {
    background-color: #d5131d;
}

#inventory_header .inventory-tab::after {
    background-color: #d5132a;
}

.sea-creature-name {
    border-bottom: 2px solid #ea0606;
}

.narrow-info-header {
    border-bottom: 2px solid #ea0606;
}

.kills-deaths-container .show-all {
    color: #f90707;
}

.tippy-tooltip[data-placement^="top"] > .tippy-arrow {
    border-top-color: #f43535;
}

.tippy-tooltip[data-placement^="bottom"] > .tippy-arrow {
    border-bottom-color: #f43535;
}
.info-header {
    background-color: #ca0b0b;
}
.pride-flag.logo {
    background: url(https://i.imgur.com/DkopHbF.png);
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 100%;
    background-position: center;
    background-repeat: no-repeat;
    width: 25px;
    height: 25px;
    margin-top: 12px;
}
html {
    background-color: #000;
    background: url(https://i.imgur.com/9hpNFVT.png) no-repeat center center fixed;
}
#bg_blur {
    background: url(https://i.imgur.com/1RSAzwP.png) no-repeat center center fixed;
    background-color: rgba(0, 0, 0, 0);
}
#enter_player_box_wrapper {
    background: url(https://i.imgur.com/1RSAzwP.png) no-repeat center center fixed;
    background-color: rgba(0, 0, 0, 0);
}
#error_title {
     background-color: #ed4553fa
}
#error_box_wrapper {
    background: url(https://i.imgur.com/1RSAzwP.png) no-repeat center center fixed;
}
[data-tippy-content]:hover:after {
    color: #e82525
}
.top-profile {
    background: url(https://i.imgur.com/1RSAzwP.png) no-repeat center center fixed;
}
.switch_packs_button{
    background-color: rgb(198, 36, 44)
}
.switch_packs_button:hover{
    background-color: rgb(245, 14, 14)
}
.kills-deaths-container .narrow-info-content{
   scrollbar-color: #d73131 transparent;
}
.minion-variant.minion-crafted
  {
    color: #d72626;
  }
}
