css:
@-moz-document domain("ezgif.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

#wrapper
{
  background: #3d3d3f;
}

#upload-form,
.form legend,

.txt,

#output,

#submenu
{
    background: #363639;
}

#menu li.active a,#menu a:hover, #menu a:active, #menu li:hover a,
#menu li ul,

.options,

.convert-btn,

.file-menu,
.file-menu a,

#news li
{
    background-color: #363639;
}

#upload-form fieldset,

input,

#footer,

.file-menu a.active,

.sub-options,

#submenu ul li.active a
{
    background: #2e2e2f !important;
}

.form input.text, .form textarea, .form select,

.ui-widget-content
{
    background: #23232599 !important;
}

body, h1, h2, h3, input, select,

#menu a,

.form legend,

.footer-menu-row strong,

.convert-btn,

.file-menu a,

#output,

#submenu ul li a,

#site-description
{
  color: #ccc !important;
}

a {
    color: #6baced;
}

*[style*="green"]
{
  color: #6dff6d !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

.sub-options:after, .sub-options:before
{
    border-color: #2e2e2f transparent;
}

.file-menu a
{
  text-shadow: none;
}

#sidebar
{
  display: none;
}

#main,
.file-menu
{
    margin: 0 auto;
    width: 80%;
}

#logo,

#output img[src*="loadcat"]
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}
}
