css:
@-moz-document domain("digi24.ro") {
body, html {
    background-color: #202020;
    color: #eee;
}
a {
    color: #eee;
}
a:hover {
    color: #fff;
}
.header {
    background-color: #111;
    box-shadow: 0 8px 0 rgba(68, 68, 68, 0.8);
}
img {
    box-shadow: 0 0 0px 1px rgba(255, 255, 255, .1);
}
.widget,
.widget-blog-economic,
.widget-blog{
    background: linear-gradient(to bottom,#111 0,#1a1a1a 100%);
}
.widget-exchange-rate ,
.widget-weather-location {
    border-color: #333;
}

ol, ul, dl,
.article-intro,
.article-tag,
.section-title,
.read-more,
figcaption,
.author-meta,
.author-meta a,
.author-meta span,
.breadcrumbs-item-link,
.breadcrumbs-item:before,
.dropdown-selection,
.dropdown-list-item-link,
.list-item,
.pagination-link{
    color: #888;
}
.widget-exchange-rate-title,
.breadcrumbs-item-link:focus,
.breadcrumbs-item-link:hover,
.read-more:focus,
.read-more:hover {
    color: #fff;
}
.article-tag{
   display: inline-block; 
}
.social-icon,
.social-link,
.nav-social-link,
.read-more:focus,
.read-more:hover {
    border-color: #999;
}
.social-icon:after,
.social-link:after,
.nav-social-link:after {
    filter: invert();
}
.bg,
.schedule-list-item-time,
.pagination-link {
    background-color: #333
}
.brdr:after,
.schedule-list-item:hover,
.pagination-link.pagination-link-current {
    background-color: #444;
}
}
