css:
@-moz-document url-prefix("https://www.vatican.va/archive"), url-prefix("http://www.vatican.va/archive") {
/*basic global stuff*/
body {
    background: #181A1B !important;
    font-family:helvetica !important;
    font-size: 16 !important;
    color: #FFFFFF !important;
}
/*new emblem 1*/
body > span > a > img {
	height: 0 !important;
	width: 0 !important;
	padding-left: 153px !important;
	padding-top:  207px !important;
    background: url(https://myimageslinks.weebly.com/uploads/1/1/8/4/118456463/published/800px-emblem-of-the-papacy-se-svg.png?1601588650) no-repeat !important;
}
/*new emblem 2*/
body > table:nth-child(2) > tbody > tr:nth-child(1) > td:nth-child(1) > a > img {
	height: 0 !important;
	width: 0 !important;
	padding-left: 153px !important;
	padding-top:  207px !important;
    background: url(https://myimageslinks.weebly.com/uploads/1/1/8/4/118456463/published/800px-emblem-of-the-papacy-se-svg.png?1601588650) no-repeat !important;
}
/*removing bar*/
body > table:nth-child(3) > tbody > tr > td {
	height: 0 !important;
	width: 0 !important;
	padding-left: 0px !important;
	padding-top:  0px !important;
    background: url(https://nskdadjsaksnsdkandsak.weebly.com/) no-repeat !important;
    
}
/*previous/next*/
body > font:nth-child(4) > center > font > b:nth-child(1) > a {
    color: #FFFFFF !important;}
body > font:nth-child(4) > center > font > b:nth-child(2) > a {
    color: #FFFFFF !important;}


/*back*/
body > table:nth-child(2) > tbody > tr:nth-child(1) > td:nth-child(2) > font > a:nth-child(2) > b > img {
	height: 0 !important;
	width: 0 !important;
	padding-left: 49px !important;
	padding-top:  28px !important;
    background: url(https://myimageslinks.weebly.com/uploads/1/1/8/4/118456463/back_orig.png) no-repeat !important;
    
}
/*up*/
body > table:nth-child(2) > tbody > tr:nth-child(1) > td:nth-child(2) > font > a:nth-child(3) > b > img {
	height: 0 !important;
	width: 0 !important;
	padding-left: 49px !important;
	padding-top:  28px !important;
    background: url(https://myimageslinks.weebly.com/uploads/1/1/8/4/118456463/up_orig.png) no-repeat !important;
    
}
}
