css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filemanager.psi-online.de") {
  body {
    background-color: #EBEBEB !important;
  }

  * {
    font-size: 12px;
    font-family: 'Arial';
  }

  .logobox {
    background-color: #9E9E9E !important;
    right: 5px !important;
    height: 60px !important;
  }

  .logobox img, .loginbox img  {
    width: 0px !important;
    height: 16px !important;
    padding-left: 16px !important;
    border: none !important;
    background-position: left center;
    background-repeat: no-repeat;
    height: 16px !important;
    top: 4px;
    position: relative;
  }

  .logobox img[src="http://www.psi-online.de/include/psilogo.gif"] {
    width: 450px !important;
    height: 50px !important;
  }

  .logobox img[src="include/rendir.gif"], img[src="include/rendir.gif"] + font {
    display: none !important;
  }

  .logobox img[src="include/renfile.gif"], .loginbox img[src="./include/rendir.gif"] {
    background-image: url("data:image/png;base64,");
  }

  .logobox img[src="include/renfile.gif"] + font:before{
    content: "Ordner oder " !important;
  }

  .logobox img[src="include/edit.gif"] {
    background-image: url("data:image/gif;base64,");
  }

  .logobox img[src="include/newfile.gif"], .loginbox img[src="./include/newfile.gif"] {
    background-image: url("data:image/gif;base64,");
  }

  .logobox img[src="include/downlfile.gif"] {
    background-image: url("data:image/png;base64,");
  }

  .logobox img[src="include/delfile.gif"] {
    background-image: url("data:image/gif;base64,");
  }

  .logobox img[src="include/deldir.gif"], .loginbox img[src="./include/deldir.gif"] {
    background-image: url("data:image/png;base64,");
  }

  .logobox img[src="include/newdir.gif"], .loginbox img[src="./include/newdir.gif"] {
    background-image: url("data:image/png;base64,");
  }
  
  .loginbox {
    background-color: transparent !important;
    border-right: 1px solid black;
    bottom: 0px;
    top: 85px !important;
    text-align: left !important;
  }

  .loginbox a[href="nutzung.php"] {
    color: black !important;
    font-size: 13px !important;
    text-decoration: none !important;
    height: 16px !important;
    padding-left: 24px !important;
    background-position: left center;
    background-repeat: no-repeat;
    height: 16px !important;
    background-image: url("data:image/png;base64,");  
  }

  .loginbox a[href="dokumentation/Kurzanleitung.pdf"] {
    color: black !important;
    font-size: 13px !important;
    text-decoration: none !important;
    height: 16px !important;
    padding-left: 24px !important;
    background-position: left center;
    background-repeat: no-repeat;
    height: 16px !important;
    background-image: url("data:image/png;base64,");  
  }

  .loginbox p {
    background-color: #FFFFFF !important;
    border: 1px highlight solid !important;
    position: absolute;
    left: 5px;
    right: 5px;
    margin: 5px !important;
    padding: 5px;
    overflow: auto !important;
  }

  .loginbox p br + a[href^="/index.php?"], .loginbox p a[href^="/index.php?directory"] {
    color: #454545 !important;
    font-size: 13px !important;
    text-decoration: none !important;
    height: 16px !important;
    padding-left: 24px !important;
    background-position: left center;
    background-repeat: no-repeat;
    height: 16px !important;
    background-image: url("data:image/png;base64,");  
  }

  .loginbox form {
    text-align: right !important;
    margin: 5px;
  }

  .loginbox input[type="text"], .loginbox input[type="password"], .loginbox form select[name="ftpaccount"] {
    width: 100% !important;
    margin-bottom: 5px;
  }

  .loginbox a[href="/index.php?action=logout"] {
    color: black !important;
    font-size: 13px !important;
    text-decoration: none !important;
    height: 16px !important;
    padding-left: 24px !important;
    background-position: left center;
    background-repeat: no-repeat;
    height: 16px !important;
    background-image: url("data:image/png;base64,");  
  }

  .loginbox a[href="/index.php?action=logout"]:after {
    content: " ...";
  }

  .contentbox {
    background-color: transparent !important;
    top: 95px !important;
  }

  .contentbox #zahlen{
    margin-top: 3px !important;
  }

  .contentbox textarea {
    min-width: 99% !important;
  }

  .contentbox br + p {
    background-color: #FFFFFF !important;
    border: 1px highlight solid !important;
    position: absolute;
    left: 5px;
    right: 5px;
    margin: 5px !important;
    padding: 5px;
    overflow: auto !important;
  }

  .contentbox p a {
    color: #454545 !important;
    font-size: 13px !important;
    text-decoration: none !important;
    height: 16px !important;
    padding-left: 24px !important;
    background-position: left center;
    background-repeat: no-repeat;
    height: 16px !important;
    background-image: url("data:image/gif;base64,");  
  }

/* DATEITYP ICONS ANFANG =================================================================================== */

  .contentbox p a[href$=".exe"], .contentbox p a[href$=".EXE"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".php"], .contentbox p a[href$=".PHP"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".html"], .contentbox p a[href$=".HTML"], .contentbox p a[href$=".htm"], .contentbox p a[href$=".HTM"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".js"], .contentbox p a[href$=".JS"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".css"], .contentbox p a[href$=".CSS"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".txt"], .contentbox p a[href$=".TXT"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".doc"], .contentbox p a[href$=".DOC"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".xls"], .contentbox p a[href$=".XLS"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".ppt"], .contentbox p a[href$=".PPT"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".pdf"], .contentbox p a[href$=".PDF"] {
    background-image: url("data:image/png;base64,");
  }

/* ARCHIVDATEIN ---------------------------------- */

  .contentbox p a[href$=".zip"], .contentbox p a[href$=".ZIP"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".rar"], .contentbox p a[href$=".RAR"] {
    background-image: url("data:image/png;base64,");
  }

/* BILDDATEIN ------------------------------------ */

  .contentbox p a[href$=".jpg"], .contentbox p a[href$=".JPG"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".png"], .contentbox p a[href$=".PNG"] {
    background-image: url("data:image/png;base64,");
  }

  .contentbox p a[href$=".gif"], .contentbox p a[href$=".GIF"] {
    background-image: url("data:image/png;base64,");
  }

/* DATEITYP ICONS ENDE ========================================================================================= */

  .contentbox p a:hover {
    text-decoration: underline !important;
  }

  .contentbox img {
    border: none !important;
    width: 0 !important;

  }

  .contentbox a[href*="/index.php?fileaction=renfile&renfile="] {
    font-size: 16px !important; 
    height: 16px !important;
    padding-left: 18px !important;
    background-position: left center;
    background-repeat: no-repeat;
    background-image: url("data:image/png;base64,") !important;
  }

  .contentbox a[href*="/index.php?fileaction=delfile&delfile="] {
    font-size: 16px !important;
    height: 16px !important;
    padding-left: 18px !important;
    background-position: left center;
    background-repeat: no-repeat;
    background-image: url("data:image/gif;base64,") !important;
  }

  .contentbox a[href^="/index.php?fileaction=downloadfile&downlfile="] {
    font-size: 16px !important;  
    height: 16px !important;
    padding-left: 18px !important;
    background-position: left center;
    background-repeat: no-repeat;
    background-image: url("data:image/png;base64,") !important;
  }

  .contentbox a[href^="/index.php?fileaction=edit&editfile="] {
    font-size: 16px !important; 
    height: 16px !important;
    padding-left: 18px !important;
    background-position: left center;
    background-repeat: no-repeat;
    background-image: url("data:image/gif;base64,") !important;
  }

  .infobox {
    background-color: transparent !important;
    padding: 0px !important;
    padding: 2px !important;
    border-bottom: 1px solid black;
    left: 236px !important;
    top: 85px !important;
    width: 755px !important;
  }

  .infobox a {
    text-decoration: none !important;
    color: orange !important;
  }

  .infobox a:before {
    content: "aktuelle Datei:";
    color: black;
    padding-right: 10px;
  }
}
